INSERT INTO `dtdoctor` (`doctor_id`, `hospital_id`, `district_id`, `hospital_name`, `hospital_level`, `doctor_name`, `doctor_position`, `doctor_des`, `doctor_head`, `good_at`, `honor`, `work_experience`, `section_info`, `hospital_section_info`, `can_disease`, `is_top`, `create_time`, `update_time`, `doctor_other_id`, `is_delete`) VALUES
(10000000001958, 10000000010106, 440100, '广东省人民医院', 1, '廖洪涛', 6, '副主任医师，医学博士，专业特长：常见血管疾病的临床工作，主要研究方向为经导管射频消融术、起搏器植入术，冠心病介入', '/upload/doctor/440100/head/ys-001.png', '擅长：常见血管疾病的临床工作，主要研究方向为经导管射频消融术、起搏器植入术，冠心病介入', NULL, NULL, '', '', '', 0, 1479788902, 1479788902, 1, 1),
(10000000001959, 10000000010106, 440100, '广东省人民医院', 1, '林展翼', 11, '主任医师，副院长，广东省老年医学研究所所长，广东省人民医院干部病区（东病区）行政主任，主任医师，硕士生导师。中华医学会老年医学分会常委，广东省医学会老年医学分会副主委，广东省医学会心血管分会委员；广东省医师协会理事，擅长：高血压病的诊断、鉴别诊断、治疗、顽固性高血压病诊治', '/upload/doctor/440100/head/ys-002.png', '擅长：高血压病的诊断、鉴别诊断、治疗、顽固性高血压病诊治', NULL, NULL, '', '', '', 0, 1479788902, 1479788902, 2, 1),
(10000000001960, 10000000010106, 440100, '广东省人民医院', 1, '吴书林', 11, '主任医师，教授，现任广东省心血管病研究所所长、广东省人民医院党委常委。医学博士，硕士、博士生导师，博士后合作导师，擅长：心律失常、心力衰竭、高血压、心肌病的诊治，尤其是导管消融术治疗各种心动过速', '/upload/doctor/440100/head/ys-003.png', '擅长：心律失常、心力衰竭、高血压、心肌病的诊治', NULL, NULL, '', '', '', 0, 1479788902, 1479788902, 3, 1),
(10000000001961, 10000000010106, 440100, '广东省人民医院', 1, '王启仪', 11, '主任医师，教授， 现任广东省医学科学院、广东省人民医院副院长，主任医师，硕士生导师。全国消化系病学会委员，广东省消化系病学会副主任委员；广东省医师协会常务理事，广东省医师协会消化分会主任委员，中国医师协会消化医师分会常委；中国医院协会护理管理专业委员会副主任委员。，教授，擅长：胃食管反流病、胃炎、消化性溃疡、炎症性肠病、胃肠道肿瘤以及肝、胆和胰腺疾病的诊治', '/upload/doctor/440100/head/ys-022.png', '擅长：胃食管反流病、胃炎、消化性溃疡、炎症性肠病、胃肠道肿瘤以及肝、胆和胰腺疾病的诊治', NULL, NULL, '', '', '', 0, 1479788902, 1479788902, 22, 1),
(10000000001962, 10000000010106, 440100, '广东省人民医院', 1, '沙卫红', 11, '主任医师，科主任，擅长：胃食管反流病、消化性溃疡等酸相关性疾病，功能性胃肠病，炎症性肠病，肝硬化及并发症，消化系肿瘤的诊断与治疗以及胃肠电生理、超声胃镜、小肠镜等检查操作，内镜下球囊扩张、支架放置、胆总管取石、套扎及注射止血、息肉切除等各种治疗', '/upload/doctor/440100/head/ys-023.png', '擅长：胃食管反流病、消化性溃疡等酸相关性疾病，功能性胃肠病，炎症性肠病，肝硬化及并发症，消化系肿瘤的诊断与治疗以及胃肠电生理、超声胃镜、小肠镜等检查操作，内镜下球囊扩张、支架放置、胆总管取石、套扎及注射止血、息肉切除等各种治疗', NULL, NULL, '', '', '', 0, 1479788902, 1479788902, 23, 1),
(10000000001963, 10000000010106, 440100, '广东省人民医院', 1, '李子俊', 11, '主任医师，教授， 2003年4月－2005年7月留学美国，在宾夕法尼亚大学医院及加洲太平洋医学中心肝病胃肠科及介入内镜中心作博士后研究和访问学者，擅长：慢性胃病、肠病、肝病、胰腺病的药物治疗和内镜治疗', '/upload/doctor/440100/head/ys-024.png', '擅长：慢性胃病、肠病、肝病、胰腺病的药物治疗和内镜治疗', NULL, NULL, '', '', '', 0, 1479788902, 1479788902, 24, 1),
(10000000001964, 10000000010106, 440100, '广东省人民医院', 1, '裴剑浩', 11, '', '/upload/doctor/440100/head/ys-025.png', '擅长治疗糖尿病、甲状腺、甲状旁腺、肾上腺、垂体、痛风、性腺等方面的疾病', '曾参与科内多次课题的立项及研究工作，口服降糖药与2GF系统；2型糖尿病并发症与2GF系统；甲亢病人骨质疏松预防治疗研究。', '30年从医经验,毕业于中山医科大学,博士学位,担任科室副主任，从事内分泌专业工作近20年，通过大量的临床实践，已熟练掌握了内分泌常见病及危重病的处理,擅长治疗糖尿病、甲状腺、甲状旁腺、肾上腺、垂体、痛风、性腺等方面的疾病。 广东省糖尿病学术委员会委员,内分泌专业博士,。 1986年毕业于中山医科大学医疗系, 发表专业论文20多篇,参与各项研究课题10多项,尤其擅长自身免疫性糖尿病的研究。中山医科大学和南方医科大学临床教学兼职教师。', '', '', '', 0, 1479788902, 1479788902, 25, 1),
(10000000001965, 10000000010106, 440100, '广东省人民医院', 1, '陈红梅', 6, '', '/upload/doctor/440100/head/ys-026.png', '擅长诊治糖尿病心血管病、糖尿病肾病、妊娠糖尿病及肥胖症', '中华医学会消化病学分会委员', '任广东省人民医院内分泌科副主任', '', '', '', 0, 1479788902, 1479788902, 26, 1),
(10000000001966, 10000000010106, 440100, '广东省人民医院', 1, '邝建', 11, '主任医师，教授，科主任，医学博士，广东省内分泌学会副主任委员，从事内分泌、糖尿病的临床和科研工作，对糖尿病、甲状腺疾病、骨质疏松、肥胖、生长迟缓等各种代谢、内分泌疾病具丰富临床经验', '/upload/doctor/440100/head/ys-027.png', '擅长：内分泌、糖尿病的临床和科研工作，对糖尿病、甲状腺疾病、骨质疏松、肥胖、生长迟缓等各种代谢、内分泌疾病具丰富临床经验', NULL, NULL, '', '', '', 0, 1479788902, 1479788902, 27, 1),
(10000000001967, 10000000010106, 440100, '广东省人民医院', 1, '陈寄梅', 11, '主任医师，现在广东心研所霍英东心脏中心，从事心血管外科的临床、科研和教学工作，着重于新生儿及小婴儿重症、复杂先心病的外科治疗和成人先心病的外科治疗，擅长：小儿及成人先天性心脏病外科治疗，新生儿及小婴儿重症复杂先心病外科手术、成人复杂、再次先心病外科治疗、小切口手术', '/upload/doctor/440100/head/ys-042.png', '擅长：小儿及成人先天性心脏病外科治疗，新生儿及小婴儿重症复杂先心病外科手术、成人复杂、再次先心病外科治疗、小切口手术', NULL, NULL, '', '', '', 0, 1479788902, 1479788902, 42, 1),
(10000000001968, 10000000010106, 440100, '广东省人民医院', 1, '范瑞新', 11, '主任医师， 广东心研所生物医学工程研究室主任。从事心血管外科临床及科研工作近20年，具有较全面的医学理论知识和丰富的临床工作经验，先后在德国心脏中心和北京阜外心血管病医院进修学习，熟练掌握各种心血管疾病的诊断和手术技术，尤其专长心脏瓣膜疾病、大血管疾病的手术治疗和先天性心脏病、冠心病的外科处理。手术效果好', '/upload/doctor/440100/head/ys-043.png', '擅长：年，具有较全面的医学理论知识和丰富的临床工作经验，先后在德国心脏中心和北京阜外心血管病医院进修学习，熟练掌握各种心血管疾病的诊断和手术技术，尤其专长心脏瓣膜疾病、大血管疾病的手术治疗和先天性心脏病、冠心病的外科处理', NULL, NULL, '', '', '', 0, 1479788902, 1479788902, 43, 1),
(10000000001969, 10000000010106, 440100, '广东省人民医院', 1, '范小平', 6, '副主任医师，副教授，医学博士，从事心血管外科的临床、科研工作20年。在先心病、心脏瓣膜病及大血管疾病等方面有较深入的研究。现为广东省心血管病研究所心外科大血管外科专业团队主要成员，中日国际合作项目“大血管手术脊髓缺血损伤的发生机制及其防治方法的研究”的主要技术负责人', '/upload/doctor/440100/head/ys-044.png', '擅长：先心病、心脏瓣膜病及大血管疾病等', NULL, NULL, '', '', '', 0, 1479788902, 1479788902, 44, 1),
(10000000001970, 10000000010106, 440100, '广东省人民医院', 1, '史伟', 11, '主任医师，教授，医学博士，广东省人民医院肾内科主任，博士生导师，临床教研室主任。中国医院协会血液净化中心管理分会常委、广东省医院协会血液净化中心管理学会主任委员擅长：系统性红斑狼疮；各种急慢性肾炎以及现代化透析中心的管理和透析质量控制方面具有丰富的临床经验和较高的学术造诣 ', '/upload/doctor/440100/head/ys-053.png', '擅长：系统性红斑狼疮；各种急慢性肾炎以及现代化透析中心的管理和透析质量控制方面具有丰富的临床经验和较高的学术造诣 ', NULL, NULL, '', '', '', 0, 1479788902, 1479788902, 53, 1),
(10000000001971, 10000000010106, 440100, '广东省人民医院', 1, '梁馨苓', 11, '', '/upload/doctor/440100/head/ys-054.png', '擅长：治疗急慢性肾功能衰竭、急慢性肾小球肾炎，狼疮性肾炎，尤其在急、慢性肾功能衰竭的血液净化技术。', '在各类专业杂志发表研究论文50余篇，获广东省科技进步三等奖1项。主持广东省科技厅等科研项目4项。', '医学博士、主任医师、教授、硕士研究生导师。广东省血液净化质控中心副主任、广东省医院协会血液净化中心管理学会常委、中华医学会肾脏病广东分学会委员。从事肾脏病专业20年。擅长治疗急慢性肾功能衰竭、急慢性肾小球肾炎，狼疮性肾炎、高血压病、糖尿病肾病、急慢性肾盂肾炎等常见肾脏疾病。 尤其在急、慢性肾功能衰竭的血液净化技术如：血液透析、血液滤过、血浆置换、血液灌流、连续性肾脏替代治疗（CRRT）、人工肝、血浆分离吸附（CPFA）等方面以及现代化透析中心的管理和质量控制方面具有丰富经验和一定的造诣。1991年中山医科大学全英班毕业，2002年获中山医科大学医学硕士学位（内科肾脏病学）。2007年南方医科大学在读博士（内科肾脏病学）。\r\n', '', '', '', 0, 1479788902, 1479788902, 54, 1),
(10000000001972, 10000000010106, 440100, '广东省人民医院', 1, '刘双信', 1, '', '/upload/doctor/440100/head/ys-055.png', '各种急慢性肾炎、肾结石、泌尿道感染、前列腺炎、高血压病、糖尿病肾病、系统性红斑狼治疗糖尿病肾病', '', '', '', '', '', 0, 1479788902, 1479788902, 55, 1),
(10000000001973, 10000000010106, 440100, '广东省人民医院', 1, '杜欣', 11, '主任医师，博士生导师。现任中山大学医学院、南方医科大学和汕头大学医学兼职教授；广东省人民医院肿瘤中心副主任，血液科行政主任。擅长：骨髓增生异常综合征,白血病，骨髓增殖性疾病，骨髓瘤和淋巴瘤等血液肿瘤；造血干细胞移植后并发移植物抗宿主病。', '/upload/doctor/440100/head/ys-065.png', '擅长：骨髓增生异常综合征,白血病，骨髓增殖性疾病，骨髓瘤和淋巴瘤等血液肿瘤；造血干细胞移植后并发移植物抗宿主病。', NULL, NULL, '', '', '', 0, 1479788902, 1479788902, 65, 1),
(10000000001974, 10000000010106, 440100, '广东省人民医院', 1, '林伟', 11, '主任医师，教授，广东省著名血液病专家。中山医科大学兼职教授、硕士研究生导师。从事血液病临床与研究30多年，积累了丰富的临床经验，擅长：急、慢性白血病，骨髓瘤，淋巴瘤，贫血性疾病，骨髓增值性疾病，出凝血疾病等疾病的诊断与治疗', '/upload/doctor/440100/head/ys-066.png', '擅长：急、慢性白血病，骨髓瘤，淋巴瘤，贫血性疾病，骨髓增值性疾病，出凝血疾病等疾病的诊断与治疗', NULL, NULL, '', '', '', 0, 1479788902, 1479788902, 66, 1),
(10000000001975, 10000000010106, 440100, '广东省人民医院', 1, '李群华', 11, '主任医师，现任中华医学会广东省血液学会常委,中华医学会广州市血液学会委员，专业与专长：熟练掌握内科血液病诊断、治疗；特别是对成人复发、难治、高危急性白血病和高度恶性淋巴瘤以及重型再生障性贫血的治疗。熟练掌握骨髓细胞形态学，分子生物学、细胞遗传学的诊断及骨髓移植;内科常见急症的处理，重大突发事件的应急处理', '/upload/doctor/440100/head/ys-067.png', '擅长：内科血液病诊断、治疗；特别是对成人复发、难治、高危急性白血病和高度恶性淋巴瘤以及重型再生障性贫血的治疗', NULL, NULL, '', '', '', 0, 1479788902, 1479788902, 67, 1),
(10000000001976, 10000000010106, 440100, '广东省人民医院', 1, '陈仁', 6, '副主任医师，副教授，擅长：各类肝病、黄疸鉴别、发热查因及各种感染性疾病和医院感染、抗生素应用等方面的诊断及治疗；特别乙型肝炎病毒的DNA转阴治疗及乙肝二对半大三阳转变成小三阳的治疗', '/upload/doctor/440100/head/ys-074.png', '擅长：各类肝病、黄疸鉴别、发热查因及各种感染性疾病和医院感染、抗生素应用等方面的诊断及治疗', NULL, NULL, '', '', '', 0, 1479788902, 1479788902, 74, 1),
(10000000001977, 10000000010106, 440100, '广东省人民医院', 1, '陈学福', NULL, '现担任中华医学会广东省传染病与寄生虫病委员会中青年委员，广东省医师协会感染科医师工作委员会常务委员，广东省肝脏病学会理事会理事。擅长：慢性病毒性肝炎的抗病毒治疗和重症肝炎、肝硬化失代偿期、肝性脑病等疾病的治疗有较深的体会和独到的治疗', '/upload/doctor/440100/head/ys-075.png', '擅长：慢性病毒性肝炎的抗病毒治疗和重症肝炎、肝硬化失代偿期、肝性脑病等疾病的治疗有较深的体会和独到的治疗', NULL, NULL, '', '', '', 0, 1479788902, 1479788902, 75, 1),
(10000000001978, 10000000010106, 440100, '广东省人民医院', 1, '陈文莉', 11, '主任医师，从事临床工作20年， 熟练掌握感染性疾病和传染性疾病的诊疗技术。对于各种肝病（包括病毒性肝炎、肝硬化、脂肪肝等）、胆道疾病、黄疸、发热、腹水查因等疾病的诊断和治疗有独到的见解。尤其对于病毒性肝炎的药物治疗和新进展有深刻的认识和丰富的临床经验', '/upload/doctor/440100/head/ys-076.png', '擅长：感染性疾病和传染性疾病的诊疗技术', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 76, 1),
(10000000001979, 10000000010106, 440100, '广东省人民医院', 1, '马冬', 11, '', '/upload/doctor/440100/head/ys-079.png', '擅长消化系统肿瘤和老年常见恶性肿瘤的多学科综合治疗', '科研成果：承担多项国内和国际性的多中心临床研究项目（包括病种有：胃癌、结肠癌、胰腺癌和甲状腺癌等），主持和承担多项省级科研课题，在国家级和省级医学刊物发表论文近20篇。', '主任医师，教授，肿瘤内科主任，1988年毕业于广州中山医科大学，专业特长：从事肿瘤内科工作二十多年，积极推动肿瘤的规范化诊断和治疗，对各种实体肿瘤、常见血液肿瘤的诊治有丰富的临床经验，尤其擅长消化系统肿瘤和老年常见恶性肿瘤的多学科综合治疗。 2001-2002年公派赴法国MONTPELIER国家肿瘤中心研修深造。现任中国抗癌协会胃肠胰神经内分泌肿瘤专家委员会成员、胰腺癌专家委员会成员；中国抗癌协会大肠癌专业委员会化疗学组成员；中国生物工程学会肿瘤分子靶向治疗专业委员会常委；广东省抗癌协会癌症康复与姑息治疗专业委员会副主任委员、化疗专业委员会副主任委员、大肠癌专业委员会常务委员、胃癌专业委员会常务委员；广东省中西医结合学会肿瘤专业委员会常务委员；广州抗癌协会常务理事、大肠癌专业委员会主任委员、肝胆胰肿瘤专业委员会主任委员；广东省中华医学会肿瘤分会内科组副组长。 ', 'a:10000000000102-b:肿瘤内科@a:10000000000062-b:内科', 'a:10000000010718-b:肿瘤内科@a:10000000000062-b:内科', '', 0, 1479788903, 1479969213, 79, 1),
(10000000001980, 10000000010106, 440100, '广东省人民医院', 1, '董秋美', 6, '副主任医师， 中山大学肿瘤学博士。从事肿瘤内科临床工作近20年，擅长消化道恶性肿瘤（胃癌、结直肠癌、食管癌、肝癌、胰腺癌）、头颈恶性肿瘤、妇科肿瘤、软组织肉瘤等多种实体瘤的化疗及多学科综合治疗', '/upload/doctor/440100/head/ys-080.png', '擅长消化道恶性肿瘤（胃癌、结直肠癌、食管癌、肝癌、胰腺癌）、头颈恶性肿瘤、妇科肿瘤、软组织肉瘤等多种实体瘤的化疗及多学科综合治疗', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 80, 1),
(10000000001981, 10000000010106, 440100, '广东省人民医院', 1, '赖晓嵘', 6, '', '/upload/doctor/440100/head/ys-081.png', '擅长：消化内科疾病、尤其是消化道肿瘤的诊治', '', '现任广东省人民医院胃肠肿瘤内科的副主任医师，临床医学博士。从事内科临床工作10余年，先后在中山大学和南方医科大学取得医学硕士、博士学位，对消化内科疾病、尤其是消化道肿瘤的诊治积累了丰富的经验。', 'a:10000000000102-b:肿瘤内科@a:10000000000062-b:内科', 'a:10000000010718-b:肿瘤内科@a:10000000000062-b:内科', '', 1, 1479788903, 1479969213, 81, 1),
(10000000001982, 10000000010106, 440100, '广东省人民医院', 1, '廖宁', 11, '主任医师，医学博士，研究生导师，广东省人民医院乳腺科主任(乳腺一科和乳腺二科)，乳腺科学科带头人，擅长：乳腺癌早期诊断，乳腺癌综合治疗（乳腺肿瘤微创手术，根治性手术，保乳手术，乳腺癌乳房重建手术', '/upload/doctor/440100/head/ys-092.png', '擅长：乳腺癌早期诊断，乳腺癌综合治疗（乳腺肿瘤微创手术，根治性手术，保乳手术，乳腺癌乳房重建手术', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 92, 1),
(10000000001983, 10000000010106, 440100, '广东省人民医院', 1, '李学瑞', 6, '', '/upload/doctor/440100/head/ys-093.png', '擅长：乳腺肿瘤的早期诊断和鉴别诊断、外科手术治疗', '主持和参与国家自然科学基金、广东省自然科学基金及广东省医学科研基金多项，在国内外杂志发表论文十余篇，参与国家“九五”攻关计划一项，并获广东省科技进步三等奖1项。', '副主任医师 医学博士\r\n广州市抗癌协会执行委员会委员，广东省抗癌协会乳腺癌专业委员会委员，研究方向：乳腺癌综合治疗及基础研究。1998年开始从事外科临床工作，擅长乳腺肿瘤的早期诊断（乳腺微创手术）及外科手术治疗（乳腺癌保乳手术及术中放疗、根治手术、乳房重建手术）；临床经验丰富，掌握国际乳腺肿瘤诊治发展，能正确诊断和治疗多种疑难乳腺疾病。2014－2015年赴德国海德堡大学曼海姆医学院主攻乳腺癌保乳手术及术中放疗技术，并在Prof. Wenz的指导下完成了“Intraoperative radiotherapy (IORT) as a boost for breast cancer: Evaluation of the Radiance? Planning System and correlation of clinical data for fibrosis”的博士课题。', 'a:10000000000069-b:甲状腺乳腺外科@a:10000000000058-b:外科', 'a:10000000010732-b:甲状腺乳腺外科@a:10000000000058-b:外科', 'a:10000000000971-b:甲状腺癌@a:10000000000972-b:甲状腺结节@a:10000000000973-b:乳腺癌@a:10000000000974-b:乳腺结节@a:10000000000975-b:甲状腺腺瘤@a:10000000000976-b:甲亢@a:10000000000977-b:单纯性甲状腺肿', 0, 1479788903, 1479969213, 93, 1),
(10000000001984, 10000000010106, 440100, '广东省人民医院', 1, '韦尉东', 11, '', '/upload/doctor/440100/head/ys-094.png', '擅长：胸部肿瘤如肺癌、食管癌、乳腺癌及纵隔肿瘤的诊断、外科及综合治疗', '近5年获省厅级研究基金资助3项，在国内核心期刊发表研究论文9篇、国外1篇。', '1987年同济大学临床医学毕业后从业普外科、烧伤外科，1997年起从业临床肿瘤学，主攻胸部肿瘤如肺癌、食管癌、乳腺癌及纵隔肿瘤的诊断、外科及综合治疗，2008年起主要研究乳腺癌外科治疗及以外科为主的综合治疗。', 'a:10000000000069-b:甲状腺乳腺外科@a:10000000000058-b:外科', 'a:10000000010732-b:甲状腺乳腺外科@a:10000000000058-b:外科', 'a:10000000000971-b:甲状腺癌@a:10000000000972-b:甲状腺结节@a:10000000000973-b:乳腺癌@a:10000000000974-b:乳腺结节@a:10000000000975-b:甲状腺腺瘤@a:10000000000976-b:甲亢@a:10000000000977-b:单纯性甲状腺肿', 0, 1479788903, 1479969213, 94, 1),
(10000000001985, 10000000010106, 440100, '广东省人民医院', 1, '钱德英', 11, '主任医师，资深主任，硕士研究生导师，广东省医学会妇产科学分会顾问，广东省医师协会妇产科分会顾问，中国癌症研究基金会子宫颈癌防治协作组委员，广东省医学会妇产科学分会宫颈病变与阴道镜学组组长，擅长：宫颈癌前期病变的早期筛查诊治及阴道镜的临床应用；对下生殖道感染疾病的诊断治疗', '/upload/doctor/440100/head/ys-106.png', '擅长：宫颈癌前期病变的早期筛查诊治及阴道镜的临床应用；对下生殖道感染疾病的诊断治疗', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 106, 1),
(10000000001986, 10000000010106, 440100, '广东省人民医院', 1, '叶海燕', 11, '', '/upload/doctor/440100/head/ys-107.png', '擅长：妇科良恶性肿瘤的手术、化疗及综合治疗、子宫内膜异位症的诊治；腹腔镜和宫腔镜手术在妇科疾病的诊治应用，子宫脱垂和压力性尿失禁的修复手术等', '主持完成多项省级科研课题，发表医学论文二十余篇。', '26年从医经验,毕业于中山医科大学,硕士学位，从医十多年，临床医学硕士，中山医科大学妇科肿瘤专业硕士研究生毕业，从事妇产科临床工作20余年，2011年曾赴美国Vanderbilt大学医学中心进修妇科肿瘤。熟练掌握妇科常见病的诊断和治疗，擅长妇科良恶性肿瘤的手术、化疗及综合治疗、子宫内膜异位症的诊治。擅长腹腔镜和宫腔镜手术在妇科疾病的诊治应用，子宫脱垂和压力性尿失禁的修复手术等。', 'a:10000000000089-b:妇科@a:10000000000060-b:妇产科', 'a:10000000010719-b:妇科@a:10000000000060-b:妇产科', 'a:10000000001091-b:宫颈癌@a:10000000001092-b:卵巢癌@a:10000000001093-b:卵巢囊肿@a:10000000001094-b:阴道癌@a:10000000001095-b:宫外孕@a:10000000001096-b:子宫癌@a:10000000001097-b:子宫肌瘤@a:10000000001098-b:子宫内膜异位症@a:10000000001099-b:子宫脱垂', 0, 1479788903, 1479969213, 107, 1),
(10000000001987, 10000000010106, 440100, '广东省人民医院', 1, '陈建国', 11, '', '/upload/doctor/440100/head/ys-108.png', '擅长妇科肿瘤早期诊断、手术治疗、术后化疗及并发症的防治', ' 主持完成省科委重点攻关项目、省卫生厅科研项目各一项。', '主任医师，科副主任，医学博士,从事妇产科临床工作近二十年，具有坚实的基础理论知识和丰富的临床经验，熟练掌握妇产科各种常见疾病的诊疗，各种常规、复杂、疑难手术指征、时机、术式选择、手术操作及并发症的防治', 'a:10000000000089-b:妇科@a:10000000000060-b:妇产科@@a:10000000000090-b:产科@a:10000000000060-b:妇产科', 'a:10000000010719-b:妇科@a:10000000000060-b:妇产科@@a:10000000010719-b:妇科@a:10000000000060-b:妇产科', 'a:10000000001091-b:宫颈癌@a:10000000001092-b:卵巢癌@a:10000000001093-b:卵巢囊肿@a:10000000001094-b:阴道癌@a:10000000001095-b:宫外孕@a:10000000001096-b:子宫癌@a:10000000001097-b:子宫肌瘤@a:10000000001098-b:子宫内膜异位症@a:10000000001099-b:子宫脱垂@a:10000000001100-b:高危妊娠@a:10000000001101-b:妊娠合并心脏病', 0, 1479788903, 1479969213, 108, 1),
(10000000001988, 10000000010106, 440100, '广东省人民医院', 1, '郑秋坚', 11, '主任医师，教授，目前担任第四届中国医师协会骨科医师分会常委、关节外科工作委员会副主任委员；第九届中华医学会骨科分会委员、关节外科学组委员、骨关节炎学组委员，擅长：股骨头坏死的综合治疗；干细胞移植治疗股骨头坏死；复杂高难度的髋关节、膝关节、肩关节置换手术；人工关节的翻修手术；关节和韧带损伤的修复和重建手术 ', '/upload/doctor/440100/head/ys-121.png', '擅长：股骨头坏死的综合治疗；干细胞移植治疗股骨头坏死；复杂高难度的髋关节、膝关节、肩关节置换手术；人工关节的翻修手术；关节和韧带损伤的修复和重建手术', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 121, 1),
(10000000001989, 10000000010106, 440100, '广东省人民医院', 1, '肖丹', 11, '', '/upload/doctor/440100/head/ys-122.png', '擅长：脊柱外科疾病的诊断和手术治疗 椎间孔镜微创技术治疗腰椎间盘突出症，腰椎管狭窄减压； 显微镜下颈椎疾病前后路手术； 显微镜下腰椎管狭窄腰椎滑脱的微创减压融合手术，脊柱微创技术', '曾多次并不间断到国内外著名的脊柱外科中心进修学习，在2013~2014年美国骨科排名第一的特种外科医院HSS做临床fellow，形成先进的精准微创的治疗理念，积极开展推广先进的脊柱骨科新技术新成果', '主任医师职称，主任医生，中山医科大学临床医学系毕业，医学博士，广东省医学会脊柱外科分会青年委员会副主任委员，著名国际脊柱学术组织AOSPINE会员，从事骨科临床工作多年，积累丰富的骨科疾病诊治经验，\r\n', 'a:10000000000085-b:脊柱外科@a:10000000000059-b:骨科', 'a:10000000010724-b:脊柱外科@a:10000000000059-b:骨科', 'a:10000000001077-b:颈椎病@a:10000000001078-b:脊柱肿瘤@a:10000000001079-b:脊柱畸形@a:10000000001080-b:脊柱骨折', 0, 1479788903, 1479969213, 122, 1),
(10000000001990, 10000000010106, 440100, '广东省人民医院', 1, '昌耘冰', 11, '主任医师，教授，从事骨科脊柱外科临床工作20余年。广东省人民医院骨科脊柱外科学术带头人，擅长： 脊柱外科各种疾病的诊断治疗 寰枢椎疾病、颈椎病和腰椎间盘疾病、腰椎管狭窄、腰椎滑脱等腰椎退变性疾病 脊柱侧弯，脊柱后凸等严重畸形 脊柱肿瘤、骨质疏松骨折', '/upload/doctor/440100/head/ys-123.png', '擅长： 脊柱外科各种疾病的诊断治疗', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 123, 1),
(10000000001991, 10000000010106, 440100, '广东省人民医院', 1, '陈良嗣', 11, '', '/upload/doctor/440100/head/ys-129.png', '擅长:耳鼻咽喉颌面头颈肿瘤的诊治。尤其擅长：颜面部皮肤肿瘤切除及美容整复、甲状腺良、恶性肿瘤手术', '南方医科大学硕士生导师，汕头大学医学院硕士生导师，中山大学医学博士，中国中西医结合学会耳鼻咽喉科专业委员会头颈肿瘤专家委员会委员，广东省抗癌协会头颈委员会委员，广东省医师协会耳鼻咽喉科分会秘书，广东省中西医结合学会耳鼻咽喉专业委员会常委，海西甲状腺美容微创外科协会委员，广州市医学会耳鼻咽喉科分会第九届委员', '曾先后赴丹麦欧登塞大学医院耳鼻咽喉头颈外科、香港中文大学威尔士亲王医院耳鼻咽喉头颈外科、中国医学科学院肿瘤医院头颈科、中山大学肿瘤医院头颈科研修交流。从事本专业工作18年，掌握本专业常见病、疑难病诊治。主要研究方向和特长为耳鼻咽喉颌面头颈肿瘤、颈部腺体微创外科、头颈先天性疾病的诊治。\r\n', 'a:10000000000100-b:耳鼻喉科@a:10000000000063-b:五官科', 'a:10000000010730-b:耳鼻喉科@a:10000000000063-b:五官科', 'a:10000000001134-b:耳聋@a:10000000001135-b:中耳炎@a:10000000001136-b:中耳癌@a:10000000001137-b:耳廓外伤@a:10000000001138-b:外耳畸形@a:10000000001139-b:鼻息肉@a:10000000001140-b:鼻咽癌@a:10000000001141-b:鼻骨骨折@a:10000000001142-b:喉癌@a:10000000001143-b:声带息肉@a:10000000001144-b:头颈部肿瘤', 0, 1479788903, 1479969213, 129, 1),
(10000000001992, 10000000010106, 440100, '广东省人民医院', 1, '崔勇', 6, '副主任医师，医学博士，从事耳鼻喉科专业十余年，毕业于复旦大学（原上海医科大学），师从中科院院士，我国著名耳鼻喉科专家王正敏教授五年，擅长：慢性及胆脂瘤性中耳炎，耳硬化症，咽鼓管异常开放症的诊断和治疗。尤其擅长各种去除病灶，改善听力的中内耳显微手术', '/upload/doctor/440100/head/ys-130.png', '擅长：慢性及胆脂瘤性中耳炎，耳硬化症，咽鼓管异常开放症的诊断和治疗。尤其擅长各种去除病灶，改善听力的中内耳显微手术', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 130, 1),
(10000000001993, 10000000010106, 440100, '广东省人民医院', 1, '董秀芹', 11, '主任医师，广东省人民医院皮肤科行政主任，硕士，临床一线工作二十年，理论基础扎实，临床经验丰富，对各种皮肤病的治疗有丰富的临床经验；擅长皮肤光学美容及白癜风、黄褐斑、银屑病、红斑狼疮、儿童皮肤病、大疱性皮肤病及疑难皮肤性病的诊治', '/upload/doctor/440100/head/ys-134.png', '擅长皮肤光学美容及白癜风、黄褐斑、银屑病、红斑狼疮、儿童皮肤病、大疱性皮肤病及疑难皮肤性病的诊治', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 134, 1),
(10000000001994, 10000000010106, 440100, '广东省人民医院', 1, '李楠', 6, '副主任医师，暨南大学医学硕士。1996年参加工作。从事皮肤性病临床工作十多年，熟悉皮肤科常见病、多发病的诊治。技术全面，熟练掌握皮肤美容、性病诊疗及各项技术操作。对于皮肤美容脱发，真菌，性传播疾病，过敏性疾病及各种顽固性皮肤病的治疗有独到之处', '/upload/doctor/440100/head/ys-135.png', '擅长：皮肤科常见病、多发病的诊治', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 135, 1),
(10000000001995, 10000000010107, 440100, '中山大学附属第一医院', 1, '何建桂', 11, '', '/upload/doctor/440100/head/ys-004.png', '擅长：各种心律失常的诊治，心律失常射频消融及起搏器治疗', '科研基金 ：先后承担十余项科研项目，在研项目3项。论著 ：先后在国内外发表论文40余篇，SCI收录论文5篇。专著：作为副主编编写《心血管危重症监护治疗学》、《心血管内科疾病临床诊断与治疗方案》，参编专著4部 ', '心內科主任医师，教授，中华医学会心脏起搏及电生理分会房颤工作组及室性心律失常工作组成员；广东省医师协会心电生理与起搏分会副主委；广东省医学会心脏起搏及电生理分会常委，广东省心血管病分会心律失常学组成员；卫生部心律失常介入培训导师；广州市老年病学会常委。广东省药学会心血管合理用药专家委员会常委。\r\n', 'a:10000000000112-b:心内科@a:10000000000062-b:内科', 'a:10000000010797-b:心内科@a:10000000000062-b:内科', '', 0, 1479788903, 1479969213, 4, 1),
(10000000001996, 10000000010107, 440100, '中山大学附属第一医院', 1, '吴素华', 11, '主任医师，教授，中山大学附属第一医院心血管医学部副主任，黄埔院区心血管内科主任，博士导师，擅长：心律失常的射频导管消融治疗和起搏器治疗以及冠心病(心肌梗死、心绞痛)的导管介入治疗和高血压、心力衰竭等的治疗', '/upload/doctor/440100/head/ys-005.png', '擅长：心律失常的射频导管消融治疗和起搏器治疗以及冠心病(心肌梗死、心绞痛)的导管介入治疗和高血压、心力衰竭等的治疗', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 5, 1),
(10000000001997, 10000000010107, 440100, '中山大学附属第一医院', 1, '唐安丽', 11, '主任医师，心内科副主任，硕士导师，掌握内科常见病诊治及心血管内科常见、疑难和危重病的诊治。从事心电生理临床工作20余年，擅长心律失常、心脏起搏、临床电生理和经导管射频消融治疗。熟悉各类型心脏起搏器植入术、快速性心律失常的射频消融治疗术。', '/upload/doctor/440100/head/ys-006.png', '擅长心律失常、心脏起搏、临床电生理和经导管射频消融治疗', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 6, 1),
(10000000001998, 10000000010107, 440100, '中山大学附属第一医院', 1, '徐评议', 11, '主任医师，教授，擅长：运动障碍/神经变性病：帕金森病、肝豆状核变性、扭转痉挛、发作性运动障碍、多系统变性、瘫痪以及面肌痉挛等肌张力障碍病 ；痴呆：血管性痴呆、老年性痴呆等', '/upload/doctor/440100/head/ys-010.png', '擅长：运动障碍/神经变性病：帕金森病、肝豆状核变性、扭转痉挛、发作性运动障碍、多系统变性、瘫痪以及面肌痉挛等肌张力障碍病 ；痴呆：血管性痴呆、老年性痴呆等', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 10, 1),
(10000000001999, 10000000010107, 440100, '中山大学附属第一医院', 1, '刘海荣', 11, '', '/upload/doctor/440100/head/ys-011.png', '擅长：脑血管疾病、癫痫、头痛、头晕、睡眠障碍、多动症、抽动症、神经痛、神经炎、焦虑、忧郁、精神异常、大脑发育不全、智力低下、肌肉病、肌无力、帕金森氏病等。', '', '主任医师，副教授 毕业于中山医科大学，毕业后留校，在中山一院神经科从事教学、科研及医疗工作至今，对诊治成人和小儿神经、精神科疾病有丰富的临床经验，尤为擅长脑血管疾病、癫痫、头痛、头晕、睡眠障碍、多动症、抽动症、神经痛、神经炎、焦虑、忧郁、精神异常、大脑发育不全、智力低下、肌肉病、肌无力、帕金森氏病等。', 'a:10000000000076-b:神经外科@a:10000000000058-b:外科@@a:10000000000092-b:小儿神经外科@a:10000000000061-b:小儿外科', 'a:10000000010764-b:神经外科@a:10000000000058-b:外科@@a:10000000010764-b:神经外科@a:10000000000058-b:外科', 'a:10000000001013-b:垂体瘤@a:10000000001014-b:脑膜瘤@a:10000000001015-b:胶质瘤@a:10000000001016-b:脑出血@a:10000000001017-b:脑动脉瘤@a:10000000001018-b:脑外伤@a:10000000001019-b:脑动静脉畸形@a:10000000001020-b:脊髓肿瘤@a:10000000001021-b:脊髓血管畸形@a:10000000001022-b:面肌痉挛@a:10000000001023-b:听神经瘤@a:10000000001024-b:三叉神经痛@a:10000000001025-b:癫痫@a:10000000001026-b:颅咽管瘤@a:10000000001027-b:烟雾病', 0, 1479788903, 1479969213, 11, 1),
(10000000002000, 10000000010107, 440100, '中山大学附属第一医院', 1, '陶玉倩', 11, '主任医师，教授，擅长：治疗脑血管疾病（包括脑梗塞、脑出血、蛛网膜下腔出血）、头晕、头痛、晕厥；老年性疾病；周围神经病；帕金森病；脊髓病；多发性硬化；重症肌无力及睡眠障碍。', '/upload/doctor/440100/head/ys-012.png', '擅长：治疗脑血管疾病（包括脑梗塞、脑出血、蛛网膜下腔出血）、头晕、头痛、晕厥；老年性疾病；周围神经病；帕金森病；脊髓病；多发性硬化；重症肌无力及睡眠障碍。', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 12, 1),
(10000000002001, 10000000010107, 440100, '中山大学附属第一医院', 1, '曾志荣', 11, '', '/upload/doctor/440100/head/ys-019.png', '擅长：炎症性肠病（溃疡性结肠炎、克罗恩病）、幽门螺杆菌相关性疾病（如慢性胃炎、消化性溃疡等）、胃食管反流病和胃肠动力障碍性疾病的临床诊治。', '幽门螺杆菌疫苗的实验研究，广东省科技进步二等奖（参与），2004\r\n幽门螺杆菌及其相关性疾病系列研究，广东省科技进步二等奖（参与），1996\r\n幽门螺杆菌及其相关性疾病系列研究，广东省医药卫生科技进步一等奖（参与），1996', '任中山大学附属第一医院消化内科副主任医师，博士学位。', 'a:10000000000103-b:消化内科@a:10000000000062-b:内科', 'a:10000000010794-b:消化内科@a:10000000000062-b:内科', '', 0, 1479788903, 1479969213, 19, 1),
(10000000002002, 10000000010107, 440100, '中山大学附属第一医院', 1, '陈旻湖', 11, '主任医师，教授，消化内科首席专家，学科带头人，副院长，博士导师，博士学历。擅长胃肠道疾病及肝胆疾病的诊断与治疗，对消化性溃疡、胃食管反流病、克罗恩病、溃疡性结肠炎、肝硬化及其并发症有丰富的诊疗经验。对消化道出血、腹水、黄疸等的诊断与鉴别诊断有独特的见解。从事消化道内窥镜的诊断与治疗多年，对消化道出血、食管胃底静脉曲张、胃肠道息肉、胆道结石的内镜治疗有丰富的临床经验。', '/upload/doctor/440100/head/ys-020.png', '擅长胃肠道疾病及肝胆疾病的诊断与治疗，对消化性溃疡、胃食管反流病、克罗恩病、溃疡性结肠炎、肝硬化及其并发症有丰富的诊疗经验', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 20, 1),
(10000000002003, 10000000010107, 440100, '中山大学附属第一医院', 1, '邢象斌', 6, '医学博士，副主任医师，中山大学附属第一医院消化内科临床工作多年。现任中华消化内镜学会麻醉镇静协作组委员，广东省抗癌协会肿瘤内镜分会委员擅长：消化道肿瘤及癌前病变的微创治疗、贲门失迟缓症治疗、胆总管结石和胆胰管良恶性狭窄治疗、消化道狭窄的扩张及支架治疗等', '/upload/doctor/440100/head/ys-021.png', '擅长：消化道肿瘤及癌前病变的微创治疗、贲门失迟缓症治疗、胆总管结石和胆胰管良恶性狭窄治疗、消化道狭窄的扩张及支架治疗等', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 21, 1),
(10000000002004, 10000000010107, 440100, '中山大学附属第一医院', 1, '王海军', 11, '', '/upload/doctor/440100/head/ys-039.png', '擅长：神经系统肿瘤工作。主攻垂体腺瘤等鞍区肿瘤，对垂体腺瘤等鞍区肿瘤的诊治有较深造诣', '', '1986年本科毕业后开始神经外科临床、教学和科研工作。临床经验丰富，有治疗神经外科各种疾病的经验，主要从事神经系统肿瘤工作。主攻垂体腺瘤等鞍区肿瘤，对垂体腺瘤等鞍区肿瘤的诊治有较深造诣，为广东省医学重点实验室---垂体肿瘤研究实验室负责人。显微镜、神经内镜和神经导航下经蝶窦微创手术治疗垂体腺瘤等鞍区肿瘤超过2500例，损伤小、疗效好，严重并发症发生率低于0.3％，治愈率、内分泌缓解率、视觉功能恢复率达国际国内领先水平。 收起↑ 1986年本科毕业后开始神经外科临床、教学和科研工作。临床经验丰富，有治疗神经外科各种疾病的经验，主要从事神经系统肿瘤工作。主攻垂体腺瘤等鞍区肿瘤，对垂体腺... 展开↓ ', 'a:10000000000076-b:神经外科@a:10000000000058-b:外科', 'a:10000000010764-b:神经外科@a:10000000000058-b:外科', 'a:10000000001013-b:垂体瘤@a:10000000001014-b:脑膜瘤@a:10000000001015-b:胶质瘤@a:10000000001016-b:脑出血@a:10000000001017-b:脑动脉瘤@a:10000000001018-b:脑外伤@a:10000000001019-b:脑动静脉畸形@a:10000000001020-b:脊髓肿瘤@a:10000000001021-b:脊髓血管畸形@a:10000000001022-b:面肌痉挛@a:10000000001023-b:听神经瘤@a:10000000001024-b:三叉神经痛@a:10000000001025-b:癫痫@a:10000000001026-b:颅咽管瘤@a:10000000001027-b:烟雾病', 0, 1479788903, 1479969213, 39, 1),
(10000000002005, 10000000010107, 440100, '中山大学附属第一医院', 1, '黄正松', 11, '', '/upload/doctor/440100/head/ys-040.png', '擅长脑血管疾病、颅内肿瘤、脊髓疾病和先天性颅脑疾病等的介入治疗和显微外科治疗', '获奖情况： 1．《复杂性连头婴分离术》 2003年广东省科技进步三等奖；第一完成人 2.《血管内治疗脑血管病的临床和基础研究》2000年黑龙江省科技进步二等奖；第一完成人 3．《血管内治疗脑血管病的临床和基础研究》1999年黑龙江省医药卫生科技进步一等奖；第一完成人 4．《脑血管疾病的介入治疗》1997年哈尔滨医科大学高新医疗技术成果奖；第一完成人 5．《复杂性、难治性颈动脉海绵窦瘘血管栓塞技术》1995年黑龙江省科技进步三等奖；第一完成人 ', '主任医师，教授。1982年白求恩医科大学临床医学系本科毕业，并于1988、1994年分别获硕士、博士学位，1995年破格晋升为教授，被评为黑龙江省跨世纪拔尖人才，博士生导师， 1997年～1998年日本北海道大学医学院高级访问学者，1999年被中山大学附属第一医院国内外公开招聘为神经外科主任、学科带头人。社会兼职：中华医学会广东省神经外科学会副主任委员，中华神经外科学会血管内治疗专业学组委员，世界神经外科联合会会员，国家执业医师考试命题委员会委员,国家自然科学基金同行评议专家，广东省科技评审专家，广州市科技评审专家，中山大学医学学位委员会委员，《中华医学杂志》特约编委，《国外医学脑血管疾病分册》常务编委，《新医学》常务编委，《中华显微外科杂志》编委，《中国神经精神疾病杂志》编委', 'a:10000000000076-b:神经外科@a:10000000000058-b:外科', 'a:10000000010764-b:神经外科@a:10000000000058-b:外科', 'a:10000000001013-b:垂体瘤@a:10000000001014-b:脑膜瘤@a:10000000001015-b:胶质瘤@a:10000000001016-b:脑出血@a:10000000001017-b:脑动脉瘤@a:10000000001018-b:脑外伤@a:10000000001019-b:脑动静脉畸形@a:10000000001020-b:脊髓肿瘤@a:10000000001021-b:脊髓血管畸形@a:10000000001022-b:面肌痉挛@a:10000000001023-b:听神经瘤@a:10000000001024-b:三叉神经痛@a:10000000001025-b:癫痫@a:10000000001026-b:颅咽管瘤@a:10000000001027-b:烟雾病', 0, 1479788903, 1479969213, 40, 1),
(10000000002006, 10000000010107, 440100, '中山大学附属第一医院', 1, '黄权', 11, '主任医师，擅长：微创显微手术和综合治疗，特别是听神经瘤，脑功能区、颅底、静脉窦旁肿瘤、脑干深部肿瘤、脑功能性疾病、颅脑先天畸形、高血压脑出血等脑血管病和颅脑外伤治疗', '/upload/doctor/440100/head/ys-041.png', '擅长：微创显微手术和综合治疗，特别是听神经瘤，脑功能区、颅底、静脉窦旁肿瘤、脑干深部肿瘤、脑功能性疾病、颅脑先天畸形、高血压脑出血等脑血管病和颅脑外伤治疗', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 41, 1),
(10000000002007, 10000000010107, 440100, '中山大学附属第一医院', 1, '区景松', 11, '主任医师，教授，我国心血管外科领域的首位长江学者特聘教授，也是国家杰出青年科学基金获得者和珠江学者特聘教授、国家外国专家局特聘专家和中山大学“百人计划”引进人才，擅长：各种心血管疾病诊断和治疗，如心脏瓣膜病、冠心病、主动脉大血管疾病、成人先天性心脏病及心脏移植等', '/upload/doctor/440100/head/ys-045.png', '擅长：各种心血管疾病诊断和治疗，如心脏瓣膜病、冠心病、主动脉大血管疾病、成人先天性心脏病及心脏移植等', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 45, 1),
(10000000002008, 10000000010107, 440100, '中山大学附属第一医院', 1, '王治平', 11, '主任医师，教授，擅长：心脏瓣膜置换手术，二尖瓣成形手术，冠心病冠状动脉搭桥手术，先天性心脏病矫治手术，大血管手术，以及心脏移植、心肺联合移植等疑难重症疾病的外科治疗，手术成功率高。尤擅先天性心脏病及瓣膜成形手术。近年来开展肺动脉内膜剥脱术治疗慢性栓塞性肺动脉高压取得良好效果，是国内少数能熟练掌握此手术方法的专家之一', '/upload/doctor/440100/head/ys-046.png', '擅长：心脏瓣膜置换手术，二尖瓣成形手术，冠心病冠状动脉搭桥手术，先天性心脏病矫治手术，大血管手术，以及心脏移植、心肺联合移植等疑难重症疾病的外科治疗，手术成功率高', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 46, 1),
(10000000002009, 10000000010107, 440100, '中山大学附属第一医院', 1, '殷胜利', 11, '', '/upload/doctor/440100/head/ys-047.png', '擅长：风湿性心脏病、老年退行性瓣膜疾病、各种先天性心脏病、复杂先天性心脏病与婴幼儿心脏病；心脏肿瘤及大血管疾病，及心脏与心肺联合移植', '荣获中山医科大学医疗成果三等奖。在国内的一级杂志发表第一作者学术论文14篇,亚洲胸心外科杂志发表第一作者论文一篇。', '主任医师,教授,硕士导师,硕士学历,毕业于1988年于中山医学院,留校附属医院。现任中华医学会及胸心外科学会会员,国际胸心外科协会（CTNETS）会员,中华实验外科杂志特约编委。', 'a:10000000000077-b:心外科@a:10000000000058-b:外科', 'a:10000000010770-b:心外科@a:10000000000058-b:外科', 'a:10000000001028-b:二尖瓣关闭不全@a:10000000001033-b:主动脉夹层@a:10000000001034-b:胸主动脉瘤@a:10000000001035-b:腹主动脉瘤@a:10000000001036-b:心脏粘液瘤', 0, 1479788903, 1479969213, 47, 1),
(10000000002010, 10000000010107, 440100, '中山大学附属第一医院', 1, '杨岫岩', 11, '主任医师，博士导师。从事临床工作二十余年，风湿免疫病专科工作十多年，对红斑狼疮、类风湿关节炎、强直性脊柱炎等经典的风湿免疫性疾病力求最合理最恰当的治疗方法。对疑难风湿免疫病，非感染性非肿瘤性炎症和长期不明原因发热的诊治有丰富的经验', '/upload/doctor/440100/head/ys-049.png', '擅长：风湿免疫病专科（如红斑狼疮、类风湿关节炎、强直性脊柱炎等经典的风湿免疫性疾病的诊断治疗）', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 49, 1),
(10000000002011, 10000000010107, 440100, '中山大学附属第一医院', 1, '杨念生', 11, '主任医师，科主任，教授，博士导师，博士学历，广东省风湿病学会常委、广东省康复医学会骨关节与风湿康复专业委员会常委，擅长：原发性肾小球疾病如IgA肾病，以及肾病综合征、血管炎肾损害、狼疮肾炎、复杂性尿路感染、急、慢性肾衰竭的诊断和治疗', '/upload/doctor/440100/head/ys-050.png', '擅长：原发性肾小球疾病如IgA肾病，以及肾病综合征、血管炎肾损害、狼疮肾炎、复杂性尿路感染、急、慢性肾衰竭的诊断和治疗', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 50, 1),
(10000000002012, 10000000010107, 440100, '中山大学附属第一医院', 1, '连帆', 6, '副主任医师，副教授，中山大学附属第一医院风湿免疫科副教授，医学博士，，长期从事风湿免疫性疾病临床及科研工作，承担内科特别是风湿内科专业本科及研究生教学工作。主持及参与多项国家级、省级科研基金，在国内外知名杂志发表论著多篇。擅长强直性脊柱炎、银屑病关节炎、类风湿关节炎、系统性红斑狼疮、皮肌炎、干燥综合征、骨关节炎等的诊断和治疗', '/upload/doctor/440100/head/ys-051.png', '擅长强直性脊柱炎、银屑病关节炎、类风湿关节炎、系统性红斑狼疮、皮肌炎、干燥综合征、骨关节炎等的诊断和治疗', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 51, 1),
(10000000002013, 10000000010107, 440100, '中山大学附属第一医院', 1, '余学清', 11, '主任医师，教授，副院长，博士导师，博士学历，国务院学位委员会学科评议组成员,广东省特聘教授-珠江学者,中华肾脏病学会侯任主任委员,广东省肾脏病学会主任委员,国际肾脏病学会全球发展委员会（东亚区）委员，擅长：原发性肾病综合征、狼疮性肾炎、尿路感染、急性肾衰竭和慢性肾衰竭（尿毒症）的治疗', '/upload/doctor/440100/head/ys-056.png', '擅长：原发性肾病综合征、狼疮性肾炎、尿路感染、急性肾衰竭和慢性肾衰竭（尿毒症）的治疗', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 56, 1),
(10000000002014, 10000000010107, 440100, '中山大学附属第一医院', 1, '尹培达', 11, '主任医师，教授，博士生导师，大学本科，广东医学会风湿病分会顾问，中华医学会风湿病杂志编委。1956年毕业于湖南医科大学。擅长于肾脏病、风湿病的诊疗，特别对肾小球疾病、肾小管间质性疾病、系统性红斑狼疮的诊断、预防和治疗有深入的研究和丰富的临床经验', '/upload/doctor/440100/head/ys-057.png', '擅长于肾脏病、风湿病的诊疗，特别对肾小球疾病、肾小管间质性疾病、系统性红斑狼疮的诊断、预防和治疗有深入的研究和丰富的临床经验', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 57, 1),
(10000000002015, 10000000010107, 440100, '中山大学附属第一医院', 1, '阳晓', 11, '主任医师，教授，肾内科主任，博士研究生导师，博士研究生，擅长：中西医结合方法诊治肾脏疾病，如隐匿性肾小球肾炎、慢性肾炎、狼疮肾炎、糖尿病肾病、尿路感染、慢性肾衰竭等', '/upload/doctor/440100/head/ys-058.png', '擅长：中西医结合方法诊治肾脏疾病，如隐匿性肾小球肾炎、慢性肾炎、狼疮肾炎、糖尿病肾病、尿路感染、慢性肾衰竭等', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 58, 1),
(10000000002016, 10000000010107, 440100, '中山大学附属第一医院', 1, '李娟', 11, '主任医师，教授，血液内科主任，博士导师，博士学历，从事内科及血液专业近30年，对血液科各种贫血、出血性疾病、各类白血病、恶性淋巴瘤、多发性骨髓瘤、原发性系统性淀粉样变性及其他血液系统恶性肿瘤有丰富的临床诊治经验，特别是在多发性骨髓瘤、原发性系统性淀粉样变性的诊治方面积累了及其丰富的经验', '/upload/doctor/440100/head/ys-068.png', '擅长：血液科各种贫血、出血性疾病、各类白血病、恶性淋巴瘤、多发性骨髓瘤、原发性系统性淀粉样变性及其他血液系统恶性肿瘤', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 68, 1),
(10000000002017, 10000000010107, 440100, '中山大学附属第一医院', 1, '罗绍凯', 11, '男，主任医师，教授，博士导师，硕士，从医近40年，擅长血液系统疾病特别是恶性血液病包括白血病、淋巴瘤、多发性骨髓瘤的诊断和治疗。在多发性骨髓瘤的诊治和恶性血液病肿瘤细胞的耐药机制及逆转其耐药方面作了较深入的研究。改善了患者的预后和生活质量。', '/upload/doctor/440100/head/ys-069.png', '擅长血液系统疾病特别是恶性血液病包括白血病、淋巴瘤、多发性骨髓瘤的诊断和治疗', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 69, 1),
(10000000002018, 10000000010107, 440100, '中山大学附属第一医院', 1, '周振海', 11, '主任医师，教授，硕士生导师，博士学历，1992年7月开始工作。广东省医学会血液病学分会秘书，广东省抗癌协会血液肿瘤专业委员会委员，具有扎实的内科学和血液学理论基础，长期从事临床一线工作，熟练地处理血液病常见病，对疑难病也积累了经验。擅长：急性白血病、出凝血疾病、多发性骨髓瘤的发病机制和治疗', '/upload/doctor/440100/head/ys-070.png', '擅长：急性白血病、出凝血疾病、多发性骨髓瘤的发病机制和治疗', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 70, 1),
(10000000002019, 10000000010107, 440100, '中山大学附属第一医院', 1, '刘旭盛', 11, '主任医师，教授，烧伤外料主任，博士导师，从事烧伤医疗，教学，科研工作20余年，对各种原因烧烫伤，电击伤，化学烧伤等，尤其特重烧伤治疗，烧伤后疤痕的手术治疗具有丰富经验。擅长：各种原因烧烫伤，电击伤，化学烧伤、特重烧伤，烧伤后疤痕的手术治疗，各种创面处理', '/upload/doctor/440100/head/ys-085.png', '擅长：各种原因烧烫伤，电击伤，化学烧伤、特重烧伤，烧伤后疤痕的手术治疗，各种创面处理', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 85, 1),
(10000000002020, 10000000010107, 440100, '中山大学附属第一医院', 1, '谢举临', 11, '', '/upload/doctor/440100/head/ys-086.png', '擅长治疗大面积危重烧伤、高压电击伤、化学烧伤、各种原因引起的瘢痕整形及体表肿瘤', '获得国家教育部科技进步一等奖1项、广东省科技进步三等奖1项、广州市科技进步三等奖1项。发表SCI收录论著13篇，中华系列杂志论文8篇。', '17年从医经验,毕业于中山大学,博士学位,担任科室副主任博士，主任医师，博士研究生导师。主持在研国家自然科学基金2项。现任中华医学会烧伤外科分会第九届委员会全青年委员、中国医师协会整形美容分会瘢痕专业组常委、广东省医学会烧伤分会委员、广州烧伤外科学会委员，《中华损伤与修复外科杂志》编委、《中华实验外科杂志》特约编辑。\r\n', 'a:10000000000081-b:烧伤科@a:10000000000058-b:外科', 'a:10000000010773-b:烧伤科@a:10000000000058-b:外科', '', 0, 1479788903, 1479969213, 86, 1),
(10000000002021, 10000000010107, 440100, '中山大学附属第一医院', 1, '汪谦', 11, '', '/upload/doctor/440100/head/ys-100.png', '擅长肝癌、胆道肿瘤、胆石症、肝硬化门脉高压症、脾脏疾病的诊断与外科治疗', '率先在国内对肝炎后脾脏功能系统研究并获得成果与奖励，在该领域内具有较高知名度，担任全国脾脏外科学组副组长，广东省外科分会副主任委员。中山大学第一附院普通外科（国家重点学科）主任；肝胆外科副主任；中华医学会外科分会脾脏外科学组副组长；广东省外科分会副主任委员；中华国际科学交流基金会临床肿瘤专业委员会副秘书长；普通外科文献编委会副总编辑；卫生部同行评议专家组委员；广东省医疗事故鉴定专家组委员；中华外科杂志（等10余种杂志）编委会编委。在国内外杂志以第一作者或通讯作者发表论文100余篇。主编和副主编专业著作6本, 参编18本。', '肝胆外科主任医师，肝胆外科教授，普外科主任，博士导师，从事肝胆脾脏外科临床工作近30年，擅长肝癌、胆道肿瘤、胆石症、肝硬化门脉高压症、脾脏疾病的诊断与外科治疗。对肝胆脾脏外科的常见病、多发病以及疑难杂病的诊治有丰富的临床经验。尤其对肝脏肿瘤和肝炎后肝硬化、门脉高压性巨脾具有深入的研究和独特的见解。主要研究方向：肝癌、肝硬化门脉高压症的基础与外科临床，脾脏外科临床与脾脏免疫。', 'a:10000000000072-b:肝胆外科@a:10000000000058-b:外科@@a:10000000000070-b:普外科@a:10000000000058-b:外科@@a:10000000000070-b:普外科@a:10000000000058-b:外科@@a:10000000000070-b:普外科@a:10000000000058-b:外科@@a:10000000000070-b:普外科@a:10000000000058-b:外科@@a:10000000000070-b:普外科@a:10000000000058-b:外科@@a:10000000000070-b:普外科@a:10000000000058-b:外科@@a:10000000000070-b:普外科@a:10000000000058-b:外科@@a:10000000000070-b:普外科@a:10000000000058-b:外科@@a:10000000000070-b:普外科@a:10000000000058-b:外科', 'a:10000000010758-b:肝胆外科@a:10000000000058-b:外科@@a:10000000010758-b:肝胆外科@a:10000000000058-b:外科@@a:10000000010758-b:肝胆外科@a:10000000000058-b:外科@@a:10000000010758-b:肝胆外科@a:10000000000058-b:外科@@a:10000000010758-b:肝胆外科@a:10000000000058-b:外科@@a:10000000010758-b:肝胆外科@a:10000000000058-b:外科@@a:10000000010758-b:肝胆外科@a:10000000000058-b:外科@@a:10000000010758-b:肝胆外科@a:10000000000058-b:外科@@a:10000000010758-b:肝胆外科@a:10000000000058-b:外科@@a:10000000010758-b:肝胆外科@a:10000000000058-b:外科', 'a:10000000000985-b:胆管癌@a:10000000000986-b:胆囊癌@a:10000000000987-b:胆囊结石@a:10000000000988-b:胆管结石@a:10000000000992-b:肝癌@a:10000000000993-b:肝脏移植@a:10000000000994-b:肝囊肿@a:10000000000995-b:胆囊息肉', 0, 1479788903, 1479969213, 100, 1),
(10000000002022, 10000000010107, 440100, '中山大学附属第一医院', 1, '华赟鹏', 6, '', '/upload/doctor/440100/head/ys-101.png', '擅长：肝胆胰肿瘤和门静脉高压症的诊治、肝胆管结石的综合治疗。掌握肝胆外科、普外科常见病、多发病的诊断和治疗', '发表中英文论著40余篇，其中第一作者文章26篇，SCI文章8篇。参编《肝脾外科学》、《普通外科特色诊疗技术》、《胰腺癌新理论新观点新技术》等，参译《肝胆胰外科学》。参加2011年国家医疗队，代表中山一院赴贵州毕节地区进行一个月的医疗支持扶贫工作，取得优异的成绩。获得《中华外科杂志》主办的“卡文杯”第一届外科中青年医师综合技能展示大赛卡文之星50强，并被聘为《中华外科杂志》特约通讯员。 ', '任中山大学附属第一医院肝胆外科副主任医师', 'a:10000000000072-b:肝胆外科@a:10000000000058-b:外科', 'a:10000000010758-b:肝胆外科@a:10000000000058-b:外科', 'a:10000000000985-b:胆管癌@a:10000000000986-b:胆囊癌@a:10000000000987-b:胆囊结石@a:10000000000988-b:胆管结石@a:10000000000992-b:肝癌@a:10000000000993-b:肝脏移植@a:10000000000994-b:肝囊肿@a:10000000000995-b:胆囊息肉', 0, 1479788903, 1479969213, 101, 1),
(10000000002023, 10000000010108, 440100, '中山大学孙逸仙纪念医院', 1, '王景峰', 11, '', '/upload/doctor/440100/head/ys-007.png', '擅长：各种人工心脏起搏器、埋藏式心脏转复除颤器（ICD）以及心脏再同步化治疗（CRT）装置的植入；对心力衰竭、心律失常、高血压、冠心病、先心病等多种心血管疾病有非常丰富的诊治经验', ' 2008年获广东省科技进步奖二等奖，“人工心脏起搏的基础与临床研究”，第一负责人；1998年获教育部科技进步奖三等奖，“紧急床边心脏起搏术的推广应用” ，第一负责人；在我国率先开展紧急床边心脏起搏的研究，成功研制出临时起搏电极以及临时起搏器/起搏分析仪，该技术已向多个省市医院普及推广，成果获教育部科技进步三等奖。　　', '主任医师，教授，副院长，心血管内科主任，党委书记，博士生导师，医学博士，中山大学心血管病研究所副所长，心血管医学部副主任。任广东省医学会心脏起搏与电生理分会主任委员、广东省医师协会心血管医师分会主任委员、中华医学会心电生理与起搏分会委员、中华医学会心电生理与起搏分会CRT工作组副组长，《中华心律失常学杂志》副总编辑、《中华医学杂志英文版》和《中国心脏起搏与心电生理杂志》等杂志编委。', 'a:10000000000101-b:心血管内科@a:10000000000062-b:内科', 'a:10000000010808-b:心血管内科@a:10000000000062-b:内科', 'a:10000000001029-b:室间隔缺损@a:10000000001030-b:房间隔缺损@a:10000000001032-b:动脉导管未闭@a:10000000001145-b:冠心病@a:10000000001146-b:心肌梗塞@a:10000000001147-b:肺动脉瓣狭窄@a:10000000001148-b:阵发性室上性心率过速@a:10000000001149-b:特发性室速@a:10000000001150-b:窦性心律过缓@a:10000000001151-b:窦性停搏@a:10000000001152-b:房颤@a:10000000001153-b:慢性心力衰竭', 1, 1479788903, 1479969213, 7, 1),
(10000000002024, 10000000010108, 440100, '中山大学孙逸仙纪念医院', 1, '聂如琼', 11, '主任医师，教授，冠状动脉介入治疗、心脏起搏器、肾动脉介入治疗、主动脉瘤和夹层介入治疗（主动脉腔内隔绝术）、下腔静脉过滤器、先天性心脏病介入治疗等心脏介入诊断和治疗技术；在急性心肌梗死的急诊介入治疗、急性心肌梗死合并严重并发症的抢救和治疗', '/upload/doctor/440100/head/ys-008.png', '擅长：冠状动脉介入治疗、心脏起搏器、肾动脉介入治疗、主动脉瘤和夹层介入治疗（主动脉腔内隔绝术）、下腔静脉过滤器、先天性心脏病介入治疗等心脏介入诊断和治疗技术', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 8, 1),
(10000000002025, 10000000010108, 440100, '中山大学孙逸仙纪念医院', 1, '刘品明', 11, '主任医师，教授，医学博士，博士研究生导师，擅长：心血管病的诊断和治疗，侧重于冠心病、高血压、高脂血症、心律失常和心力衰竭等疾病及心血管病的介入性诊治', '/upload/doctor/440100/head/ys-009.png', '擅长：心血管病的诊断和治疗，侧重于冠心病、高血压、高脂血症、心律失常和心力衰竭等疾病及心血管病的介入性诊治', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 9, 1),
(10000000002026, 10000000010108, 440100, '中山大学孙逸仙纪念医院', 1, '程桦', 11, '主任医师，教授，博士研究生导师。享受国务院特殊津贴专家，现任中华医学会内分泌学分会常务委员、广东省医学会内分泌学分会主任委员及多种医学杂志编委及通讯编委。研究糖尿病及肥胖症的病因、发病机理、治疗和预防。擅长：糖尿病、甲状腺疾病、垂体肾上腺疾病、肥胖症、血脂异常、骨质疏松及性腺疾病的诊断和治疗', '/upload/doctor/440100/head/ys-028.png', '擅长：糖尿病、甲状腺疾病、垂体肾上腺疾病、肥胖症、血脂异常、骨质疏松及性腺疾病的诊断和治疗', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 28, 1),
(10000000002027, 10000000010108, 440100, '中山大学孙逸仙纪念医院', 1, '严励', 11, '主任医师，教授，副院长，内分泌内科主任，内科副主任，工会主席，博士生导师，擅长：内分泌系统疾病，如糖尿病及其并发症、甲状腺疾病、垂体-肾上腺疾病、 肥胖症、脂代谢紊乱等的诊疗', '/upload/doctor/440100/head/ys-029.png', '擅长：内分泌系统疾病，如糖尿病及其并发症、甲状腺疾病、垂体-肾上腺疾病、 肥胖症、脂代谢紊乱等的诊疗', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 29, 1),
(10000000002028, 10000000010108, 440100, '中山大学孙逸仙纪念医院', 1, '李焱', 11, '内分泌专科主任医师，教授，内分泌内科副主任，硕士研究生导师，擅长：内科内分泌常见病的诊治。在糖尿病、甲状腺、肾上腺、垂体疾病的诊治和治疗方面有一定的造旨', '/upload/doctor/440100/head/ys-030.png', '擅长：内科内分泌常见病的诊治。在糖尿病、甲状腺、肾上腺、垂体疾病的诊治和治疗方面有一定的造旨', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 30, 1),
(10000000002029, 10000000010108, 440100, '中山大学孙逸仙纪念医院', 1, '刘尚礼', 11, '主任医师，教授，原骨科部主任，孙逸仙脊柱中心主任，硕士导师，博士和博士后导师，外科主任导师，1943年出生，广东省惠州市人。兼任广东省医学会骨科分会名誉会长，擅长：小儿骨科，脊柱外科特别是脊柱侧凸的三维矫正、股骨头缺血性坏死、骨肿瘤等方面有极高的造诣', '/upload/doctor/440100/head/ys-119.png', '擅长：小儿骨科，脊柱外科特别是脊柱侧凸的三维矫正、股骨头缺血性坏死、骨肿瘤等方面有极高的造诣', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 119, 1),
(10000000002030, 10000000010108, 440100, '中山大学孙逸仙纪念医院', 1, '黄霖', 6, '副主任医师，医学博士，硕士生导师，现任中山大学孙逸仙纪念医院脊柱外科副主任，擅长：脊柱肿瘤；颈椎病骨质疏松性椎体压缩性骨折；腰椎退行性疾病；脊柱感染、脊柱结核；脊柱畸形；胸椎管狭窄症；脊柱骨折、脊髓损伤', '/upload/doctor/440100/head/ys-120.png', '擅长：脊柱肿瘤；颈椎病骨质疏松性椎体压缩性骨折；腰椎退行性疾病；脊柱感染、脊柱结核；脊柱畸形；胸椎管狭窄症；脊柱骨折、脊髓损伤', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 120, 1),
(10000000002031, 10000000010108, 440100, '中山大学孙逸仙纪念医院', 1, '席丽艳', 11, '主任医师，教授，擅长：各种皮肤病，尤其是感染性皮肤病，各种真菌感染性疾病（浅部真菌病：体股癣、手足癣、甲癣，皮下组织真菌感染性疾病：着色芽生菌病、孢子丝菌病。深部真菌病：念珠菌、曲霉、马尔尼菲青霉等。黏膜真菌感染：尤其是念珠菌性阴道炎等）', '/upload/doctor/440100/head/ys-131.png', '擅长：各种皮肤病，尤其是感染性皮肤病，各种真菌感染性疾病', NULL, NULL, '', '', '', 0, 1479788903, 1479788903, 131, 1),
(10000000002032, 10000000010108, 440100, '中山大学孙逸仙纪念医院', 1, '张军民', 11, '', '/upload/doctor/440100/head/ys-132.png', '擅长：诊断和治疗皮肤真菌病，对银屑病、皮肤血管炎及某些大疱性皮肤病治疗有一定经验', '', '副主任医师，副教授，医学博士，硕士生导师，从事皮肤性病临床研究二十余年，1964年出生，1987年7月同济医科大学本科毕业，1992年7月获医学硕士学位。主要研究方向：皮肤真菌病。负责及参与研究的课题有：“锁德CΔ40通路对银屑病的阻抑作用”；“从协同刺激分子探讨雷公藤治疗SLE的免疫机制”；“深部真菌病的早期诊断研究”等。医疗专长：擅长诊断和治疗皮肤真菌病（如手足癣、念珠菌病、头癣等）。对银屑病、皮肤血管炎及某些大疱性皮肤病治疗有一定经验。', 'a:10000000000108-b:皮肤病科@a:10000000000066-b:皮肤科', 'a:10000000010843-b:皮肤病科@a:10000000000066-b:皮肤科', '', 0, 1479788903, 1479969213, 132, 1),
(10000000002033, 10000000010108, 440100, '中山大学孙逸仙纪念医院', 1, '许怀麟', 6, '皮肤科副主任医师，继续医学教育科科长，从事皮肤性病专业临床工作20余年，1983年7月毕业于中山医学院（本科）。熟悉各种常见皮肤病的诊治，对少见皮肤病的诊治也有较深入的研究，尤其对性传播性疾病的诊治有深入的研究', '/upload/doctor/440100/head/ys-133.png', '擅长：各种常见皮肤病的诊治，对少见皮肤病的诊治也有较深入的研究，尤其对性传播性疾病的诊治有深入的研究', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 133, 1),
(10000000002034, 10000000010109, 440100, '珠江医院', 1, '杨卫红', 6, '副主任医师，副教授，硕士（在读博士）。目前主要从事脑血管疾病的临床治疗与研究，发表专业论文20余篇。专著《中西医结合治疗脑血管病》的编委，神经病学医师协会会员，中华医学会神经病学分会广东分会会员，擅长：脑血管疾病、癫痫的临床治疗', '/upload/doctor/440100/head/ys-013.png', '擅长：脑血管疾病、癫痫的临床治疗', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 13, 1),
(10000000002035, 10000000010109, 440100, '珠江医院', 1, '赵连旭', 6, '副主任医师，副教授，医学博士后，神经内科微创颅内血肿穿刺治疗的负责人，对遗传变性疾病和各种心理疾病、身心疾病，特别对帕金森病的干细胞治疗、基因治疗的基础理论进行了深入的研究。获得广东省科技进步二等奖一项，国家发明专利和实用新型专利各一项，干细胞研究著作一篇，发表SCI收录论文二篇，核心期刊论文近十篇。擅长遗传性、变性性神经疾病及心理疾病', '/upload/doctor/440100/head/ys-014.png', '擅长遗传性、变性性神经疾病及心理疾病', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 14, 1),
(10000000002036, 10000000010109, 440100, '珠江医院', 1, '马伯扬', 11, '主任医师，教授，研究生导师，著名神经内科专家，籍贯台山市。1961年毕业于中国人民解放军第四军医大学。毕业后一直在军医大学从事医疗、教学和科研工作。多年来先后培养和指导过一大批各级临床医师、硕士研究生、博士研究生，擅长：癜痫、中枢神经系统感染性疾病、神经系统脱髓鞘疾病和脑血管病的诊治', '/upload/doctor/440100/head/ys-015.png', '擅长：癜痫、中枢神经系统感染性疾病、神经系统脱髓鞘疾病和脑血管病的诊治', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 15, 1),
(10000000002037, 10000000010110, 440100, '南方医科大学南方医院', 1, '刘思德', 11, '主任医师，教授，博士生导师，广东省政协委员，目前担任南方医院消化科主任。长期从事结肠镜诊疗及早期大肠癌内镜诊治研究，擅长：肝病治疗及结肠镜诊疗', '/upload/doctor/440100/head/ys-016.png', '擅长：肝病治疗及结肠镜诊疗', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 16, 1),
(10000000002038, 10000000010110, 440100, '南方医科大学南方医院', 1, '智发朝', 11, '主任医师，教授，科副主任，博士研究生导师，学术带头，擅长胆胰疾病的内镜微创治疗和小肠疾病的临床诊疗，在溃疡性结肠炎和克罗恩病的基础研究及诊疗方面有较深的造诣。', '/upload/doctor/440100/head/ys-017.png', '擅长胆胰疾病的内镜微创治疗和小肠疾病的临床诊疗，在溃疡性结肠炎和克罗恩病的基础研究及诊疗方面有较深的造诣。', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 17, 1),
(10000000002039, 10000000010110, 440100, '南方医科大学南方医院', 1, '王继德', 11, '', '/upload/doctor/440100/head/ys-018.png', '擅长：慢性胃病，肠道免疫与炎症性疾病及消化系肿瘤的基础与临床诊治', '以第一或主要完成人获得国家科技进步二等奖1项，军队科技进步二等奖2项，三等奖1项，美国Mclaughlin基金会优秀研究奖等。在国外知名学术刊物发表论著10余篇，国内130余篇。中华医学会消化病学分会青年委员会副主任委员、中国微生态学会委员和临床学组副组长，中华消化学会幽门螺杆菌学组副组长，广东省消化学会委员和秘书等。', '毕业于第一军医大学,博士学位,担任科室副主任，消化内科副主任、副主任医师、教授，在第一军医大学相继获得医学和临床医学本科、硕士和博士学位，后相继赴美国德克萨斯大学和香港大学玛丽医院内科进行博士后培训近6年。大学毕业后一直在第一军医大学(现南方医科大学)南方医院消化科工作，先后任住院、主治和副主任医师。主要从事慢性胃病，肠道免疫与炎症性疾病及消化系肿瘤的基础与临床诊治。', 'a:10000000000103-b:消化内科@a:10000000000062-b:内科', 'a:10000000010881-b:消化内科@a:10000000000062-b:内科', '', 0, 1479788904, 1479969213, 18, 1),
(10000000002040, 10000000010110, 440100, '南方医科大学南方医院', 1, '侯凡凡', 11, '女，主任医师，教授，肾内科主任，中国科学院院士，博士生导师，现为南方医科大学肾脏病研究所所长，兼任中华肾脏病学会副主任委员、香港肾脏教育研究基金会客座教授等学术职务和14家国内外期刊的栏目主编、副主编或编委，擅长：急、慢性肾炎、肾病综合征、狼疮性肾炎等肾脏病的诊治；原发和继发性肾脏疾病、急慢性肾功能衰竭、血液净化', '/upload/doctor/440100/head/ys-059.png', '擅长：急、慢性肾炎、肾病综合征、狼疮性肾炎等肾脏病的诊治；原发和继发性肾脏疾病、急慢性肾功能衰竭、血液净化', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 59, 1),
(10000000002041, 10000000010110, 440100, '南方医科大学南方医院', 1, '刘宏发', 6, '副主任医师，讲师，师从著名肾脏病学专家侯凡凡院士、张训教授从事肾脏病临床研究工作。现任南方医科大学肾脏病研究所讲师，南方医院肾内科副主任医师。擅长于各种急慢性肾脏病，急、慢性肾功能衰竭的诊断治疗。在处理伴随各种合并症的肾脏病，如妊娠、感染等，以及动静脉内瘘，腹膜透析植管，肾脏活检手术等方面具有丰富的临床经验', '/upload/doctor/440100/head/ys-060.png', '擅长于各种急慢性肾脏病，急、慢性肾功能衰竭的诊断治疗', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 60, 1),
(10000000002042, 10000000010110, 440100, '南方医科大学南方医院', 1, '梁敏', 11, '主任医师，教授，历任南方医院肾内科医师、助教、主治医生、讲师、副教授、副主任医师，擅长：原发性肾小球肾炎、继发性肾损害的诊断与治疗，肾功能衰竭及其并发症的防治与肾替代治疗', '/upload/doctor/440100/head/ys-061.png', '擅长：原发性肾小球肾炎、继发性肾损害的诊断与治疗，肾功能衰竭及其并发症的防治与肾替代治疗', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 61, 1),
(10000000002043, 10000000010110, 440100, '南方医科大学南方医院', 1, '孙竞', 11, '主任医师，教授，硕士生导师，造血干细胞移植和血友病关怀诊治。从事造血干细胞移植近20年，负责完成移植600余例，居华南地区之最；重点研究非亲缘骨髓移植、难治性白血病移植、CD34筛选细胞移植治疗红斑狼疮等自身免疫性疾病，以及移植并发症防治。同时负责血友病诊治教育工作，尤其预防治疗和抑制物免疫耐受治疗等，担任全国血友病护理工作组与预防治疗组组长及华南地区血友病协作网协调人', '/upload/doctor/440100/head/ys-062.png', '擅长：造血干细胞移植和血友病关怀诊治', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 62, 1),
(10000000002044, 10000000010110, 440100, '南方医科大学南方医院', 1, '冯茹', 11, '主任医师，教授，血液科副主任，硕士生导师，长期从事血液病临床及相关基础研究，特别是有关恶性血液病的诊断与治疗、造血干细胞特性、调控方面及临床应用方面，具有丰富的诊断、治疗、随访等方面的经验。', '/upload/doctor/440100/head/ys-063.png', '擅长：有关恶性血液病的诊断与治疗、造血干细胞特性、调控方面', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 63, 1),
(10000000002045, 10000000010110, 440100, '南方医科大学南方医院', 1, '刘晓力', 11, '主任医师，教授，擅长：白血病、淋巴瘤和多发性骨髓瘤的分层诊断，以分子细胞遗传学检测评估预后及指导个体化治疗和分子靶向治疗；大剂量化疗和造血干细胞移植后的微小残留病检测及处理；各种贫血和出血性疾病的诊断及治疗。 ', '/upload/doctor/440100/head/ys-064.png', '擅长：白血病、淋巴瘤和多发性骨髓瘤的分层诊断', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 64, 1),
(10000000002046, 10000000010110, 440100, '南方医科大学南方医院', 1, '彭劼', 11, '主任医师，副教授，医学博士。南方医院感染内科及肝病中心副主任，广东省感染病学会副主任委员、广东省肝病学会理事，中华医学会感染病分会青年委员。广东省医学会医疗事故技术鉴定专家库成员，从事肝病的临床、教学、科研工作20余年，熟悉急慢性病毒性肝炎和脂肪肝及肝硬化的诊治，擅长肝组织临床病理，并结合肝组织病理指导慢性肝炎的抗病毒治疗', '/upload/doctor/440100/head/ys-071.png', '擅长肝组织临床病理，并结合肝组织病理指导慢性肝炎的抗病毒治疗', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 71, 1),
(10000000002047, 10000000010110, 440100, '南方医科大学南方医院', 1, '周福元', 6, '', '/upload/doctor/440100/head/ys-072.png', '擅长：各种急慢性病毒性肝炎的诊治和慢性乙型病毒性肝炎抗病毒的治疗', '在国内外专业杂志发表相关论文20余篇，获军队医疗成果奖2项，广东省科技进步一等奖1项。', '南方医院感染内科副主任医师、医学博士。从事传染病学临床、教学与科研近20年，熟悉各种急慢性病毒性肝炎的诊治和慢性乙型病毒性肝炎抗病毒的治疗，多次成功主持和参与危重病人的救治，负责人工肝治疗。', '', '', '', 0, 1479788904, 1479788904, 72, 1),
(10000000002048, 10000000010110, 440100, '南方医科大学南方医院', 1, '周元平', 11, '主任医师，教授，广东省感染病科医师协会副主任委员，擅长各种肝胆疾病，特别是病毒性肝炎、肝衰竭、肝硬化及其并发症的诊疗。富有中西医结合治疗肝胆疾病(尤其是慢性肝炎和肝纤维化)的临床经验。', '/upload/doctor/440100/head/ys-073.png', '擅长：各种肝胆疾病，特别是病毒性肝炎、肝衰竭、肝硬化及其并发症的诊疗', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 73, 1),
(10000000002049, 10000000010110, 440100, '南方医科大学南方医院', 1, '黄宇琦', 11, '广东省肝脏病学会肝癌委员会委员，医学博士，副主任医师，副教授，擅长： 肝癌 ；肝硬化；慢性乙肝 （脂肪肝、酒精肝、黄疸、门脉高压、肝血管瘤、肝囊肿、肝脓肿、肝腹水等肝脏疾病；胆道结石、胆道肿瘤（含胆囊）等胆道疾病；胰腺炎、胰腺癌、胰腺囊肿、胰管结石等胰腺疾病；脾肿大、脾亢、脾肿瘤等脾脏疾病；肝脏移植', '/upload/doctor/440100/head/ys-103.png', '擅长： 肝癌 ；肝硬化；慢性乙肝 等胆道疾病；胰腺炎、胰腺癌、胰腺囊肿、胰管结石等胰腺疾病；脾肿大、脾亢、脾肿瘤等脾脏疾病；肝脏', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 103, 1),
(10000000002050, 10000000010110, 440100, '南方医科大学南方医院', 1, '邹衍泰', 6, '副主任医师，副教授。医学硕士。广东省医学会微创外科分会普通外科学组副组长，广东省肝脏病学会肝癌专业委员会常务委员中华医学会会员。从事普通外科、肝胆外科临床工作20多年，对肝脏、胆道、胰腺、脾脏等疾病的外科诊断治疗有丰富的临床经验，在合并糖尿病、心脏病老年患者的围手术期处理方面，积累了丰富临床经验', '/upload/doctor/440100/head/ys-104.png', '擅长：肝脏、胆道、胰腺、脾脏等疾病的外科诊断治疗', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 104, 1),
(10000000002051, 10000000010110, 440100, '南方医科大学南方医院', 1, '沈宏伟', 6, '副主任医师，副教授，硕士导师，中华医学会妇科肿瘤学分会青年委员，长期从事妇科肿瘤的临床工作，擅长妇科肿瘤的规范化治疗，对卵巢癌、子宫内膜癌、宫颈癌和外阴癌的根治性手术治疗有丰富临床经验', '/upload/doctor/440100/head/ys-105.png', '擅长：妇科肿瘤的规范化治疗，对卵巢癌、子宫内膜癌、宫颈癌和外阴癌的根治性手术治疗', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 105, 1),
(10000000002052, 10000000010110, 440100, '南方医科大学南方医院', 1, '彭宏', 9, '医学博士，国家留学基金资助公派留学瑞士博士后，副教授，副主任医师，硕士研究生导师，擅长：以提高听力为目的的耳显微外科手术；鼻窦炎内窥镜微创手术；儿童耳鼻咽喉科疾病的诊断与治疗', '/upload/doctor/440100/head/ys-127.png', '擅长：以提高听力为目的的耳显微外科手术；鼻窦炎内窥镜微创手术；儿童耳鼻咽喉科疾病的诊断与治疗', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 127, 1),
(10000000002053, 10000000010110, 440100, '南方医科大学南方医院', 1, '田文栋', 6, '副主任医师，耳鼻咽喉头颈外科疾病的诊疗，主要研究方向：耳鼻咽喉及其它头颈部肿瘤的诊断与治疗；如喉癌、下咽癌的显微镜下微创激光治疗，头颈部恶性肿瘤的根治性手术及综合治疗，鼻腔鼻窦、前颅底疾病的鼻内镜手术及开放性手术治疗，喉科良性疾病的显微激光外科手术、阻塞性睡眠暂停低通气综合征的手术治疗等', '/upload/doctor/440100/head/ys-128.png', '擅长：耳鼻咽喉及其它头颈部肿瘤的诊断与治疗', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 128, 1),
(10000000002054, 10000000010111, 440100, '广州医学院第一附属医院', 1, '陈荣昌', 11, '主任医师，教授，擅长：支气管哮喘、肺部感染、慢性阻塞性肺疾病的诊疗。在呼吸内科常见病和少见病方面及危重症救治方面，在呼吸力学、隔肌疲劳、无创通气等方面均有一定造诣。    ', '/upload/doctor/440100/head/ys-031.png', '擅长：支气管哮喘、肺部感染、慢性阻塞性肺疾病的诊疗', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 31, 1),
(10000000002055, 10000000010111, 440100, '广州医学院第一附属医院', 1, '郑劲平', 11, '', '/upload/doctor/440100/head/ys-032.png', '擅长：阻塞性气道疾病（支气管哮喘、慢性阻塞性肺疾病等）、肺功能临床诊断、及其他常见及疑难呼吸道疾病的诊断治疗。', '发表论文70多篇，参编专著8部。', '广州医学院第一附属医院、广州呼吸疾病研究所教授，主任医师，硕士生导师。目前担任中华医学会变态反应学会委员、中华医学会呼吸病学会中青年委员兼肺功能和危重监护学组成员、中国吸烟与健康协会委员、广东医学会变态反应学会常委、广州呼吸病学会常委，任《Chest》中文版编委、《国际呼吸杂志》编委。从事呼吸内科学的临床医疗、科研和教学工作20余年。重点进行呼吸生理、肺功能检测及气道反应性技术的研究，专长于呼吸系统疾病如哮喘、慢性支气管炎、肺气肿、肺心病、肺部感染、呼吸衰竭等疾病的诊断治疗以及临床新药研究。主持广医一院和呼研所的肺功能检查。', 'a:10000000000104-b:呼吸内科@a:10000000000062-b:内科', 'a:10000000010894-b:呼吸内科@a:10000000000062-b:内科', '', 0, 1479788904, 1479969213, 32, 1),
(10000000002056, 10000000010111, 440100, '广州医学院第一附属医院', 1, '张洁霞', 11, '', '/upload/doctor/440100/head/ys-033.png', '擅长各类肿瘤内科治疗，包括肺癌、肠癌、胃癌、食管癌、乳腺癌、鼻咽癌、淋巴瘤、纵隔肿瘤，肉瘤化疗，结合放疗、手术和介入消融治疗和放射性粒子植入，个体化综合治疗和放射免疫靶向治疗', '在国内和国外刊物发表论文近30篇，在多个肿瘤相关的学术团体中担任委员或理事等职务。 ', '肿瘤内科学主任医师，硕士研究生导师，兼南生班导师。1990年6月毕业后，到广州医学院第一附属医院从事肿瘤血液内科工作，2008年6月取得南方医科大学临床医学博士学位。2008年12月至2009年5月在中山大学肿瘤防治中心进修。2013年12月至2014年4月作为美国加州大学戴维斯分校肿瘤中心访问学者并建立国际合作关系。2011年至今于广州医科大学附属第一医院广州呼吸疾病研究所从事肿瘤内科工作。', 'a:10000000000102-b:肿瘤内科@a:10000000000062-b:内科', 'a:10000000010895-b:肿瘤内科@a:10000000000062-b:内科', '', 0, 1479788904, 1479969213, 33, 1),
(10000000002057, 10000000010111, 440100, '广州医学院第一附属医院', 1, '何建行', 11, '主任医师，教授，院长，现任广州医学院第一附属医院院长、胸外科主任，呼吸疾病国家临床医学研究中心肺癌组学术带头人，美国外科学会Fellow、AATS会员。何建行教授从事胸外科工作30年，擅长：肺部肿瘤的早期诊断与治疗、复杂胸部外科手术治疗、晚期慢性阻塞性肺疾病的治疗、微创伤胸外科、胸部器官移植', '/upload/doctor/440100/head/ys-089.png', '擅长：肺部肿瘤的早期诊断与治疗、复杂胸部外科手术治疗、晚期慢性阻塞性肺疾病的治疗、微创伤胸外科、胸部器官移植', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 89, 1),
(10000000002058, 10000000010111, 440100, '广州医学院第一附属医院', 1, '韦兵', 6, '副主任医师，广东省微创外科学会胸外科学组委员，曾在广东省心血管疾病研究所进修一年，擅长：微创治疗肺癌、支气管扩张、肺大疱及肺气肿等肺疾病', '/upload/doctor/440100/head/ys-090.png', '擅长：微创治疗肺癌、支气管扩张、肺大疱及肺气肿等肺疾病', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 90, 1),
(10000000002059, 10000000010111, 440100, '广州医学院第一附属医院', 1, '杨海虹', 6, '副主任医师，副教授，胸部肿瘤学博士，从事胸部肿瘤临床工作十余年，擅长中晚期肺癌特别是肺癌脑转移、骨转移的综合治疗；擅长于胸部淋巴瘤及软组织肉瘤肺转移的综合治疗，晚期肿瘤的治疗现已趋向于慢性疾病，因此多学科的综合治疗及维持治疗包括射频消融等局部治疗以及生物免疫治疗等越来越重要，目的是延长生存，改善生存质量', '/upload/doctor/440100/head/ys-091.png', '擅长中晚期肺癌特别是肺癌脑转移、骨转移的综合治疗；胸部淋巴瘤及软组织肉瘤肺转移的综合治疗', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 91, 1),
(10000000002060, 10000000010112, 440100, '广州医学院第二附属医院', 1, '陈飞鹏', 11, '主任医师，教授，呼吸内科主任，硕士研究生导师、中华医学会广东省呼吸学会委员、中华医学会广东结核病分会常委，广州市呼吸学会副主任委员，擅长：慢性咳嗽、胸腔积液、支气管哮喘、慢性支气管炎，阻塞性肺气肿、肺部肿瘤、肺炎、肺间质纤维化、支气管扩张和睡眠呼吸障碍等疾病的诊治', '/upload/doctor/440100/head/ys-034.png', '擅长：慢性咳嗽、胸腔积液、支气管哮喘、慢性支气管炎，阻塞性肺气肿', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 34, 1),
(10000000002061, 10000000010112, 440100, '广州医学院第二附属医院', 1, '田利奇', 11, '主任医师，副教授，毕业于湘雅医学院呼吸内科研究生硕士，从事呼吸内科医疗、教学、科研工作30年，对呼吸系统各种常见疾病及疑难危重症具有丰富的临床诊疗经验。擅长肺部感染性疾病、支气管哮喘、慢性阻塞性肺疾病、慢性咳嗽、间质性肺疾病、胸腔积液、肺部肿瘤等呼吸系统疾病的诊断与治疗、擅长纤维支气管镜对于呼吸系统疾病的诊断和治疗', '/upload/doctor/440100/head/ys-035.png', '擅长纤维支气管镜对于呼吸系统疾病的诊断和治疗', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 35, 1),
(10000000002062, 10000000010113, 440100, '广州军区总医院', 1, '王伟民', 11, '主任医师，教授，科主任，医学博士后、博士生导师。现任广州军区广州总医院神经外科及脑病中心主任，《中国微侵袭神经外科杂志》主编。专长颅脑外伤、神经肿瘤、癫痫及脑血管外科治疗，2002年在国内率先开展术中唤醒状态下大脑功能区手术，创建外科手术新理念，达国际先进水平。  ', '/upload/doctor/440100/head/ys-036.png', '擅长：颅脑外伤、神经肿瘤、癫痫及脑血管外科治疗', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 36, 1),
(10000000002063, 10000000010113, 440100, '广州军区总医院', 1, '王国良', 11, '主任医师，教授，擅长：各种神经外科疾病的诊断与治疗，尤其是颅脑损伤的综合救治、各部位神经系统肿瘤的显微外科手术、颅内动脉瘤的开颅夹闭术、垂体腺瘤的经鼻蝶神经内镜和显微手术、锁孔微创手术、神经导航手术和脑功能区的唤醒手术等。', '/upload/doctor/440100/head/ys-037.png', '擅长：各种神经外科疾病的诊断与治疗，尤其是颅脑损伤的综合救治、各部位神经系统肿瘤的显微外科手术、颅内动脉瘤的开颅夹闭术、垂体腺瘤的经鼻蝶神经内镜和显微手术、锁孔微创手术、神经导航手术和脑功能区的唤醒手术等。', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 37, 1),
(10000000002064, 10000000010113, 440100, '广州军区总医院', 1, '梁军潮', 11, '医学博士、教授、主任医师、研究生导师、广州军区广州总医院全军神经外科中心副主任，神经外科副主任兼伽玛刀治疗研究中心主任。从事神经外科临床26年，对颅脑疾病的诊治有很丰富的经验，并有良好的医、教、研能力，擅长立体定向与伽玛刀放射外科手术。曾二赴瑞典、奥地利等国外著名的神经外科中心进修学习，是国内早期从事伽玛刀治疗专家之一。', '/upload/doctor/440100/head/ys-038.png', '擅长立体定向与伽玛刀放射外科手术', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 38, 1),
(10000000002065, 10000000010113, 440100, '广州军区总医院', 1, '王建华', 6, '副主任医师，副教授，擅长：采用经口咽手术治疗各类难复性寰枢椎脱位，颅底凹陷症；采用颈椎前后路小切口减压手术治疗颈椎病，椎管狭窄症；应用脊柱椎弓根钉矫形技术治疗各类脊柱侧弯 ', '/upload/doctor/440100/head/ys-118.png', '擅长：采用经口咽手术治疗各类难复性寰枢椎脱位，颅底凹陷症；采用颈椎前后路小切口减压手术治疗颈椎病，椎管狭窄症；应用脊柱椎弓根钉矫形技术治疗各类脊柱侧弯 ', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 118, 1),
(10000000002066, 10000000010113, 440100, '广州军区总医院', 1, '徐哲', 6, '副主任医师，中山大学中山眼科中心博士，美国耶鲁大学博士后。美国眼科学会会员。专业方向：玻璃体视网膜疾病，黄斑病，白内障。亚洲防盲基金会优秀个人，擅长：玻璃体视网膜疾病，血管增生相关性眼底病，黄斑病，复杂眼外伤的手术治疗，白内障超声乳化手术，青光眼，视神经病  ', '/upload/doctor/440100/head/ys-124.png', '擅长：玻璃体视网膜疾病，血管增生相关性眼底病，黄斑病，复杂眼外伤的手术治疗，白内障超声乳化手术，青光眼，视神经病 ', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 124, 1),
(10000000002067, 10000000010113, 440100, '广州军区总医院', 1, '余洪华', 6, '副主任医师，美国哈佛大学眼科博士后，中山大学眼科博士，长期从事眼科临床工作，擅长白内障、眼外伤、视网膜脱离和玻璃体视网膜疾病的诊治及手术，完成白内障超声乳化及玻璃体视网膜手术等眼科手术上万例', '/upload/doctor/440100/head/ys-125.png', '擅长白内障、眼外伤、视网膜脱离和玻璃体视网膜疾病的诊治及手术', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 125, 1),
(10000000002068, 10000000010114, 440100, '中山大学附属第六医院', 1, '黄建林', 12, '中山六院风湿免疫科专科主任，学科带头人，主任医师，硕士研究生导师擅长：类风湿关节炎、系统性红斑狼疮、脊柱关节炎、干燥综合征、皮肌炎/多肌炎、系统性硬化症、白塞病、混合性结缔组织病的诊治', '/upload/doctor/440100/head/ys-048.png', '擅长：类风湿关节炎、系统性红斑狼疮、脊柱关节炎、干燥综合征、皮肌炎/多肌炎、系统性硬化症、白塞病、混合性结缔组织病的诊治', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 48, 1),
(10000000002069, 10000000010115, 440100, '广东省第二人民医院', 1, '邓伟明', 6, '副主任医师，内科学硕士，广东省中西医结合学会风湿免疫专业委员会委员，广东省康复医学会专业委员会学组委员，世界中医药学会联合会中医药免疫专业委员会理事，擅长：直性脊柱炎、系统性红斑狼疮、类风湿关节炎、痛风、骨关节炎、硬皮病、皮肌炎等风湿免疫病的诊治', '/upload/doctor/440100/head/ys-052.png', '擅长：直性脊柱炎、系统性红斑狼疮、类风湿关节炎、痛风、骨关节炎、硬皮病、皮肌炎等风湿免疫病的诊治', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 52, 1),
(10000000002070, 10000000010116, 440100, '中山大学附属肿瘤医院', 1, '黄慧强', 11, '主任医师，教授，内科副主任，内科四区区长，博士生导师，国家药品食品监督管理局(SFDA)药物评审咨询专家，广东省抗癌协会淋巴瘤专业委员会副主任委员，广东省抗癌协会血液肿瘤专业委员会副主任委员，擅长：恶性肿瘤化疗，尤其对恶性淋巴瘤、头颈癌/鼻咽癌的化学治疗以及造血干细胞移植治疗复发难治淋巴瘤、儿童肿瘤、生殖细胞肿瘤等恶性肿瘤。', '/upload/doctor/440100/head/ys-077.png', '擅长：恶性肿瘤化疗，尤其对恶性淋巴瘤、头颈癌/鼻咽癌的化学治疗以及造血干细胞移植治疗复发难治淋巴瘤、儿童肿瘤、生殖细胞肿瘤等恶性肿瘤。', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 77, 1),
(10000000002071, 10000000010116, 440100, '中山大学附属肿瘤医院', 1, '姜文奇', 4, '二级教授，一级主任医师，博士生导师；肿瘤内科主任，现任中山大学附属肿瘤医院大内科主任，淋巴瘤中心主任，国家抗肿瘤新药临床试验中心学术委员会主任，中山大学临床药理研究所副所长等，擅长：肿瘤内科综合治疗（包括化疗、内分泌治疗和生物治疗）；恶性淋巴瘤、胃肠道肿瘤以及头颈部和胸部肿瘤等。', '/upload/doctor/440100/head/ys-078.png', '擅长：肿瘤内科综合治疗；恶性淋巴瘤、胃肠道肿瘤以及头颈部和胸部肿瘤等。', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 78, 1),
(10000000002072, 10000000010116, 440100, '中山大学附属肿瘤医院', 1, '张兰军', 11, '主任医师，教授，中国抗癌协会广东肿瘤营养专业委员会副主任委员、国际肺癌研究协会(IASLC)会员、中国医师学会胸外科医师分会委员、广东省人体生物组织工程学会理事、中华医学会广东肠外肠内营养学会常委，擅长：胸部肿瘤诊断、外科手术和综合治疗，尤其擅长食管癌及肺癌的诊断治疗', '/upload/doctor/440100/head/ys-087.png', '擅长：胸部肿瘤诊断、外科手术和综合治疗，尤其擅长食管癌及肺癌的诊断治疗', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 87, 1),
(10000000002073, 10000000010116, 440100, '中山大学附属肿瘤医院', 1, '傅剑华', 11, '主任医师，教授，副院长，肿瘤防治中心副主任，博士，EMBA，博士生导师，擅长：食管癌、肺癌及胸部肿瘤复杂的外科手术治疗及胸腔镜微创治疗，对食管癌/肺癌的综合治疗、胸部肿瘤非血管介入手术', '/upload/doctor/440100/head/ys-088.png', '擅长：食管癌、肺癌及胸部肿瘤复杂的外科手术治疗及胸腔镜微创治疗，对食管癌/肺癌的综合治疗、胸部肿瘤非血管介入手术', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 88, 1),
(10000000002074, 10000000010116, 440100, '中山大学附属肿瘤医院', 1, '尧凯', 6, '副主任医师，副教授，擅长：掌握和熟悉各类泌尿男生殖肿瘤的诊断、手术和综合治疗，能熟练完成各种泌尿肿瘤的常规手术和疑难复杂手术（下腔静脉取栓，复杂肾部分切和再次腹盆腔会阴手术等），尤其擅长阴茎癌和会阴整形手术', '/upload/doctor/440100/head/ys-098.png', '擅长：掌握和熟悉各类泌尿男生殖肿瘤的诊断、手术和综合治疗，能熟练完成各种泌尿肿瘤的常规手术和疑难复杂手术', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 98, 1),
(10000000002075, 10000000010116, 440100, '中山大学附属肿瘤医院', 1, '刘炜卓', 6, '副主任医师，副教授，擅长：泌尿系肿瘤的微创手术（腹腔镜：保留肾单位的肾癌根治术/腹膜后淋巴结清扫术/膀胱癌根治术/前列腺癌根治术/肾癌根治术等），膀胱癌、睾丸癌等泌尿系肿瘤的综合治疗', '/upload/doctor/440100/head/ys-099.png', '擅长：泌尿系肿瘤的微创手术，膀胱癌、睾丸癌等泌尿系肿瘤的综合治疗', NULL, NULL, '', '', '', 0, 1479788904, 1479788904, 99, 1);
