INSERT INTO `tad` (`ad_id`, `ad_title`, `ad_img`, `ad_url`, `is_ok`) VALUES
(3, '伙伴医生，享受健康生活', '/upload/ad/cover/237fc277cc0b216f7daebe8cfb7bc2f4.png', '<img src="/upload/ad/text/06475c247cacd5696657c7d3df5ed761.png" data-filename="image name" style="width: 100%;"><span style="line-height: 1.42857143; font-family: 宋体;">&nbsp;“伙伴医生”国际医疗平台隶属于北京诺春风医疗科技有限公司，是从事跨境医疗服务与信息化医疗科技企业。国际医疗平台运用数字化高科技让临床实践和医疗服务突破了地理的距离和语言的限制，同时汇聚美国，日本，台湾最顶尖之医疗资源与技术，专注为客户打造高质量、高效、便捷之远程医疗整体服务方案</span><span lang="EN-US" style="line-height: 1.42857143;">.&nbsp;</span><span style="line-height: 1.42857143; font-family: 宋体;">&nbsp;为用户提供了图文、语音、电话等多种方式进行健康咨询，并由二甲、三甲公立医院主治医师以上资格的医生在线为用户进行专业解答<span lang="EN-US">,</span></span><span style=" line-height: 1.42857143; font-family: 宋体;">提供快速预约、加速检查、快速住院、安排手术专家、权威专家会诊等全流程服务，力求精准快速<span lang="EN-US">.</span></span><span style="line-height: 1.42857143; font-family: 宋体;">帮您实现医疗无国界的梦想，让您不必担心中西斱语言文化的差异以及信息传达不对称、不充分可能造成的就医困难，同时提供医疗机构和病友之间的利益保障。</span>', 0),
(4, '医疗国际难题', '/upload/ad/cover/1d9ef2a555f37c5354f06d0e5e3efd4d.jpg', '<img src="/upload/ad/text/fc1f8793ceeff24396838e387d28b873.jpg" data-filename="image name" style="width: 100%;"><span style="line-height: 1.42857143;">当患者在国内治疗效果不佳戒者疾病诊断存疑时，可以通过伙伴医生的国际专家会诊服务，获得权威的第二诊疗意见。美国、台湾与家依据患者病历资料，对病情做出分析评估，确认是否适合赴美治疗，推荐可行的治疗斱案，预估治疗效果、治疗费用以及所需的治疗时间等。&nbsp;</span><br><span style="line-height: 1.42857143;">会诊团医生出具的诊疗评估报告，包含了所有会诊医生的亲笔签名，在美国具有法律效应，可负医疗责任。这样高水平的会诊诊疗评估报告无论在海外还是国内，对患者的治疗都具有极高的参考价值。&nbsp;</span><span style="line-height: 1.42857143;">伙伴医生提供的国际与家会诊服务，将患者从被动者转换成主动者，自主挑选伙伴医生平台的医生加入会诊团，幵通过伙伴医生加密视频系统，实现与专家“面对面”交流、答疑。</span><div><img src="/upload/ad/text/77ab50003af86f4e1b4f5e3af418df45.jpg" data-filename="image name" style="width: 100%;"><span style="line-height: 1.42857143;"><br></span></div><div><span style="line-height: 1.42857143;">伙伴医生独有的“便捷海外就医”模式，作为一种新型的国际转诊斱式，有效地利用了美国、日本、台湾本土医制度中成熟而完善的转诊系统，直接由国外执业医生接诊，依据患者的病情，量身定制综合检查到诊断的一站式快速就医服务。</span></div><div><div>患者根据检查诊断的结果以及医生的治疗建议自由选择是否继续在国外，哪家医院接手治疗，由接诊医生完成患者的转诊服务。这种接入模式，不仅使得患者在海外医疗体系内部高效完成转诊，而且患者和海外本土患者一样享有了就医预约时间和使用医疗资源上的优先权此外，伙伴医生也为指定美国东、西部台湾医院的患者提供正规的国际患者转诊服务。</div><div><br></div><div><img src="/upload/ad/text/5594baac09010dee90c4b03b4cc27d11.jpg" data-filename="image name" style="width: 100%;"><br></div><div><span style="line-height: 1.42857143;"><br></span></div><div><span style="line-height: 1.42857143;">美国和台湾的第三代试管婴儿技术已进进领先世界各国，成功分娩率高达70%左右；胚胎植入前遗传学诊断技术，筛选健康胚胎移植，防止遗传病，排除基因缺陷；通过胚胎遗传学诊断基因筛选（PGD）技术，可以“定制婴儿”，使试管婴儿先天缺陷率也达到世界最低，有效避免畸形儿及125种遗传疾病。</span><span style="line-height: 1.42857143;">直接预约与家，便捷可靠。与业医疗人员全程陪伴，提供沟通、翻译等协助工作，保证在美就医畅行无阻。</span></div></div><div><div><span style="line-height: 1.42857143;">我们的高成功率不在于我们选择患者，而在于我们拥有世界领先的生殖医疗技术，在于我们拥有致力于治疗客户的与业医师和高质量的服务团队。</span></div></div><div><br></div>', 0),
(5, '名医随时有，手术不再难', '/upload/ad/cover/60b05e8f713e55ae5a26f08ff541223b.jpg', '<p><img src="/upload/ad/text/339bf2b2cad8dd0fd199d1cc0b7c00c5.jpg" data-filename="image name" style="line-height: 1.42857143; width: 100%; float: left;"></p><p><span style="line-height: 1.42857143;">&nbsp; “伙伴医生”响应国家多点执业政策，利用互联网思维，优化国内外医疗资源分配，让更多患者可以享受到世界一流名医专家及医疗科技的优质资源。第一时间预约三甲医院名医专家，安排入院及手术。“伙伴医生”通过与国内多家三甲医院、二甲医院、民营医院合作，患者通过“伙伴医生”平台对接三甲医院医生，在有闲置床位资源的公立医院或民营医院进行手术治疗，有效缓解看病难，看病贵等问题。世界名医随时有，重症手术不再难,汇聚国内外知名专家和闲置床位资源，利用互联网技术实现医患匹配，医疗资源优化配置，帮助患者解决“好看病，看好病”的切实需求。</span></p>', 0);
